import React, { useEffect, useState } from 'react';
import axios from 'axios';

const planDisplayNames = {
  starter: 'Starter',
  premium: 'Premium',
  pro: 'Pro',
};

const Subscription = () => {
  const [subs, setSubs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [msg, setMsg] = useState('');

  useEffect(() => {
    const fetchSubscriptions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-subscriptions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubs(res.data.subscriptions || []);
      } catch (err) {
        setError('Could not fetch subscriptions.');
      }
      setLoading(false);
    };
    fetchSubscriptions();
  }, [msg]);

  let current = null;
  let history = [];
  if (subs.length > 0) {
    current = subs.find(s => s.status === 'active' || s.status === 'pending') || subs[0];
    history = subs.filter(s => s._id !== current._id);
  }
  const daysLeft = current && current.expiresAt ? Math.ceil((new Date(current.expiresAt) - Date.now()) / (1000 * 60 * 60 * 24)) : null;
  const isExpired = current && current.expiresAt && new Date(current.expiresAt) < new Date();

  const handleCancel = async () => {
    setMsg('');
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/auth/cancel-subscription/${current._id}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMsg('Plan cancellation requested.');
    } catch (err) {
      setMsg('Could not cancel plan.');
    }
  };

  const handleRenew = () => {
    window.location.href = '/plans';
  };

  return (
    <div style={{ maxWidth: 600, margin: '0 auto', color: '#E5E7EB' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>My Subscription</h2>
      {loading ? (
        <div style={{ color: '#A0AEC0', textAlign: 'center', marginTop: '2rem' }}>Loading...</div>
      ) : error ? (
        <div style={{ color: '#FF6B35', textAlign: 'center', marginTop: '2rem' }}>{error}</div>
      ) : subs.length === 0 ? (
        <div style={{ color: '#A0AEC0', textAlign: 'center', marginTop: '2rem' }}>No subscriptions found.</div>
      ) : (
        <>
          <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', marginBottom: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
            <div style={{ marginBottom: '1rem' }}><b>Current Plan:</b> {planDisplayNames[current.plan]}</div>
            <div style={{ marginBottom: '1rem' }}><b>Status:</b> {current.status}</div>
            <div style={{ marginBottom: '1rem' }}><b>Expires At:</b> {current.expiresAt ? new Date(current.expiresAt).toLocaleDateString() : 'N/A'}</div>
            {current.status === 'rejected' && (
              <div style={{ color: '#FF6B35', marginBottom: '1rem' }}><b>Rejection Reason:</b> {current.rejectionReason}</div>
            )}
            {daysLeft !== null && daysLeft <= 10 && current.status === 'active' && !current.canceled && !isExpired && (
              <div style={{ color: '#FF6B35', marginBottom: '1rem', fontWeight: 600 }}>Your plan expires in {daysLeft} days. <button style={{ background: '#2ECC71', color: '#181A20', border: 'none', borderRadius: '0.5rem', padding: '0.5rem 1.2rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer', marginLeft: '0.7rem' }} onClick={handleRenew}>Renew</button></div>
            )}
            {msg && <div style={{ color: '#2ECC71', marginBottom: '1rem' }}>{msg}</div>}
            {current.status === 'active' && !current.canceled && !isExpired && (
              <button onClick={handleCancel} style={{ background: '#FF6B35', color: '#fff', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer', marginTop: '1rem' }}>Cancel My Plan</button>
            )}
            {current.canceled && !isExpired && (
              <div style={{ color: '#FF6B35', marginTop: '1rem' }}>Plan will be canceled after {new Date(current.expiresAt).toLocaleDateString()}.</div>
            )}
            {isExpired && (
              <button onClick={handleRenew} style={{ background: '#2ECC71', color: '#181A20', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer', marginTop: '1rem' }}>Renew</button>
            )}
          </div>
          <h3 style={{ color: '#2ECC71', fontWeight: 600, marginBottom: '1rem' }}>Subscription History</h3>
          {history.length === 0 ? <div style={{ color: '#A0AEC0', marginBottom: '1rem' }}>No previous subscriptions.</div> : null}
          {history.map((h, i) => (
            <div key={h._id} style={{ background: '#23272F', borderRadius: '1rem', padding: '1.2rem', marginBottom: '1rem', boxShadow: '0 1px 6px rgba(0,0,0,0.07)' }}>
              <div><b>Plan:</b> {planDisplayNames[h.plan]}</div>
              <div><b>Status:</b> {h.status}</div>
              <div><b>Expires At:</b> {h.expiresAt ? new Date(h.expiresAt).toLocaleDateString() : 'N/A'}</div>
              {h.status === 'rejected' && <div style={{ color: '#FF6B35' }}><b>Rejection Reason:</b> {h.rejectionReason}</div>}
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default Subscription;
