import React from 'react';

const Services = () => {
  const services = [
    {
      id: 1,
      title: 'Custom Web Development',
      description: 'Tailored websites built with best practices',
      icon: 'üíª',
      features: ['React/Next.js', 'Node.js Backend', 'Database Design', 'API Integration'],
      color: '#1A237E',
      gradient: 'linear-gradient(135deg, #1A237E 0%, #00ACC1 100%)'
    },
    {
      id: 2,
      title: 'E-commerce Solutions',
      description: 'Complete online store development with payment integration',
      icon: 'üõí',
      features: ['Payment Gateways', 'Inventory Management', 'Order Processing', 'Analytics'],
      color: '#00ACC1',
      gradient: 'linear-gradient(135deg, #00ACC1 0%, #1A237E 100%)'
    },
    {
      id: 3,
      title: 'Mobile-First Design',
      description: 'Responsive designs optimized for all devices',
      icon: 'üì±',
      features: ['Progressive Web Apps', 'Mobile Optimization', 'Touch Interfaces', 'Performance'],
      color: '#FF9800',
      gradient: 'linear-gradient(135deg, #FF9800 0%, #1A237E 100%)'
    },
    {
      id: 4,
      title: 'Cloud & DevOps',
      description: 'Scalable cloud infrastructure and deployment solutions',
      icon: '‚òÅÔ∏è',
      features: ['AWS/Azure Setup', 'CI/CD Pipelines', 'Monitoring', 'Security'],
      color: '#00ACC1',
      gradient: 'linear-gradient(135deg, #00ACC1 0%, #FF9800 100%)'
    }
  ];

  return (
    <section className="services-section">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">Our Services</h2>
          <p className="section-subtitle">
            We provide comprehensive web development solutions tailored to your business needs
          </p>
        </div>

        <div className="services-grid">
          {services.map((service, index) => (
            <div key={service.id} className="service-card" style={{animationDelay: `${index * 0.1}s`}}>
              <div className="service-header">
                <div
                  className="service-icon"
                  style={{background: service.gradient}}
                >
                  {service.icon}
                </div>
                <h3 className="service-title">{service.title}</h3>
              </div>

              <p className="service-description">{service.description}</p>

              <ul className="service-features">
                {service.features.map((feature, idx) => (
                  <li key={idx} className="feature-item">
                    <span className="feature-check">‚úì</span>
                    {feature}
                  </li>
                ))}
              </ul>

              <a href="#contact" className="service-link">
                Learn More
                <span className="link-arrow">‚Üí</span>
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
