import React, { useContext, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, setUser } = useContext(UserContext);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const dashboardNavLinks = user && user.role === 'admin' ? [
    { to: '/admin-dashboard/user-plans', label: 'User Plans', icon: 'ðŸ‘¥' },
    { to: '/admin-dashboard/notifications', label: 'Notifications', icon: 'ðŸ””' },
    { to: '/admin-dashboard/plans', label: 'Plans', icon: 'ðŸ“¦' },
  ] : [
    { to: '/dashboard/subscription', label: 'Subscription', icon: 'ðŸ“¦' },
    { to: '/dashboard/purchases', label: 'My Purchases', icon: 'ðŸ›’' },
    { to: '/dashboard/notifications', label: 'Notifications', icon: 'ðŸ””' },
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  // Responsive: show dashboard links and user dropdown after login, else show public links
  return (
    <>
      <nav className="modern-navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-logo">
            <span className="logo-text">CUSTOM WEB</span>
          </Link>
          <div className="navbar-nav desktop-nav">
            {!user && navLinks.map(link => (
              <Link
                key={link.to}
                to={link.to}
                className={`nav-link${location.pathname === link.to ? ' active' : ''}`}
              >
                {link.label}
              </Link>
            ))}
            {user && dashboardNavLinks.map(link => (
              <Link
                key={link.to}
                to={link.to}
                className={`nav-link${location.pathname === link.to ? ' active' : ''}`}
              >
                <span style={{ marginRight: 6 }}>{link.icon}</span>{link.label}
              </Link>
            ))}
          </div>
          <div className="navbar-auth">
            {!user && (
              <>
                <Link to="/login" className="nav-link">Login</Link>
                <Link to="/register" className="nav-link">Register</Link>
              </>
            )}
            {user && (
              <div className="user-dropdown-wrapper">
                <button
                  className="user-btn user-dropdown-toggle"
                  onClick={() => setDropdownOpen(v => !v)}
                  aria-haspopup="true"
                  aria-expanded={dropdownOpen}
                >
                  <span className="user-avatar">{user.name ? user.name[0].toUpperCase() : 'U'}</span>
                  <span className="user-name">{user.name}</span>
                  <span className="dropdown-caret">â–¼</span>
                </button>
                {dropdownOpen && (
                  <div className="user-dropdown-menu">
                    {user.role === 'admin' ? (
                      <Link to="/admin-dashboard/user-plans" className="dropdown-item">Dashboard</Link>
                    ) : (
                      <Link to="/dashboard/subscription" className="dropdown-item">Dashboard</Link>
                    )}
                    {/* <Link to="/profile" className="dropdown-item">Profile</Link> */}
                    <button className="dropdown-item logout-btn" onClick={handleLogout}>Logout</button>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </nav>
      {/* Mobile Bottom Nav for logged-in users */}
      {user && (
        <nav className="mobile-bottom-nav-bar">
          {dashboardNavLinks.map(link => (
            <Link key={link.to} to={link.to} className={`bottom-nav-btn${location.pathname === link.to ? ' active' : ''}`}>
              <span className="bottom-nav-icon">{link.icon}</span>
              <span className="bottom-nav-label">{link.label}</span>
            </Link>
          ))}
          <button className="bottom-nav-btn logout" onClick={handleLogout}>
            <span className="bottom-nav-icon">ðŸšª</span>
            <span className="bottom-nav-label">Logout</span>
          </button>
        </nav>
      )}
      <style>{`
        .modern-navbar {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: rgba(35, 39, 47, 0.97);
          backdrop-filter: blur(10px);
          border-bottom: 1.5px solid rgba(255, 255, 255, 0.13);
          z-index: 1000;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          min-height: 40px;
          box-shadow: 0 2px 12px rgba(0,0,0,0.10);
        }
        .navbar-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 0.2rem 0;
          min-height: 40px;
        }
        .navbar-logo {
          text-decoration: none;
          z-index: 1001;
          margin-right: 1.2rem;
        }
        .logo-text {
          font-weight: 800;
          font-size: clamp(1rem, 2vw, 1.15rem);
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          letter-spacing: 1px;
        }
        .navbar-nav {
          display: flex;
          align-items: center;
          gap: clamp(1.2rem, 3vw, 2.2rem);
        }
        .nav-link {
          color: #E5E7EB;
          text-decoration: none;
          font-weight: 500;
          font-size: clamp(0.85rem, 2vw, 0.95rem);
          padding: 0.2rem 0;
          position: relative;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .nav-link.active,
        .nav-link:hover {
          color: #2ECC71;
        }
        .nav-link.active::after,
        .nav-link:hover::after {
          width: 100%;
        }
        .nav-link::after {
          content: '';
          position: absolute;
          bottom: -2px;
          left: 0;
          width: 0;
          height: 2px;
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          transition: width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .navbar-auth {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .user-dropdown-wrapper {
          position: relative;
          display: inline-block;
        }
        .user-btn.user-dropdown-toggle {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background: none;
          border: none;
          color: #E5E7EB;
          font-weight: 600;
          font-size: 0.95rem;
          cursor: pointer;
          padding: 0.2rem 0.7rem 0.2rem 0.5rem;
          border-radius: 2rem;
          transition: background 0.2s;
        }
        .user-btn.user-dropdown-toggle:hover, .user-btn.user-dropdown-toggle:focus {
          background: rgba(46,204,113,0.10);
        }
        .user-avatar {
          width: 1.7rem;
          height: 1.7rem;
          background: linear-gradient(135deg, #2ECC71, #0057D9 80%);
          color: #fff;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1rem;
          font-weight: 700;
          box-shadow: 0 2px 8px rgba(46,204,113,0.13), 0 1px 4px rgba(0,87,217,0.10);
          border: 2px solid #181A20;
        }
        .user-name {
          font-weight: 700;
          color: #E5E7EB;
          letter-spacing: 0.2px;
        }
        .dropdown-caret {
          font-size: 0.8rem;
          margin-left: 0.2rem;
        }
        .user-dropdown-menu {
          position: absolute;
          top: 110%;
          right: 0;
          min-width: 160px;
