import React, { useState } from 'react';

const SupportChat = () => {
  const [messages, setMessages] = useState([
    { from: 'support', text: 'Hello! How can we help you today?' }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = e => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setInput('');
    setTimeout(() => {
      setMessages(msgs => [...msgs, { from: 'support', text: 'Thank you for your message! (Demo reply)' }]);
    }, 1000);
  };

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '500px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>Help & Support Chat</h2>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)', minHeight: '300px', marginBottom: '1rem' }}>
        <div style={{ maxHeight: '200px', overflowY: 'auto', marginBottom: '1rem' }}>
          {messages.map((msg, i) => (
            <div key={i} style={{ textAlign: msg.from === 'user' ? 'right' : 'left', margin: '0.5rem 0' }}>
              <span style={{ background: msg.from === 'user' ? '#2ECC71' : '#0057D9', color: '#fff', borderRadius: '1rem', padding: '0.5rem 1rem', display: 'inline-block', maxWidth: '80%' }}>
                {msg.text}
              </span>
            </div>
          ))}
        </div>
        <form onSubmit={sendMessage} style={{ display: 'flex', gap: '0.5rem' }}>
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: 1, padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }}
          />
          <button type="submit" style={{ background: '#2ECC71', color: '#181A20', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.2rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Send</button>
        </form>
      </div>
    </div>
  );
};

export default SupportChat;
