import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// Simple JWT decode (no validation, just base64 decode)
function getUserFromToken() {
  const token = localStorage.getItem('token');
  if (!token) return null;
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload;
  } catch {
    return null;
  }
}

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  const user = getUserFromToken();
  return (
    <nav className="responsive-flex" style={{
      background: '#23272F',
      color: '#E5E7EB',
      padding: '0.75rem 2rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      boxShadow: '0 2px 8px rgba(0,0,0,0.04)',
      flexWrap: 'wrap',
      gap: '0',
    }}>
      <div style={{ fontWeight: 700, fontSize: '1.5rem', letterSpacing: '1px', color: '#2ECC71' }}>
        CUSTOM WEB
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '2rem' }}>
        <div style={{ display: 'flex', gap: '1.5rem' }}>
          {navLinks.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={{
                color: location.pathname === link.to ? '#2ECC71' : '#E5E7EB',
                textDecoration: 'none',
                fontWeight: location.pathname === link.to ? 700 : 500,
                borderBottom: location.pathname === link.to ? '2px solid #FF6B35' : '2px solid transparent',
                padding: '0.25rem 0',
                transition: 'border 0.2s, color 0.2s',
              }}
              onMouseOver={e => e.target.style.color = '#FF6B35'}
              onMouseOut={e => e.target.style.color = location.pathname === link.to ? '#2ECC71' : '#E5E7EB'}
            >
              {link.label}
            </Link>
          ))}
        </div>
        <div style={{ height: '2rem', width: '1px', background: '#E5E7EB', margin: '0 1.5rem' }} />
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          {user ? (
            <span style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.1rem', background: '#181A20', padding: '0.4rem 1.1rem', borderRadius: '1.2rem' }}>
              {user.name || user.email || 'User'}
            </span>
          ) : (
            <>
              <Link to='/login' style={{
                background: '#0057D9',
                color: '#E5E7EB',
                padding: '0.4rem 1.1rem',
                borderRadius: '1.2rem',
                fontWeight: 700,
                textDecoration: 'none',
                border: '2px solid #0057D9',
                transition: 'background 0.2s, color 0.2s',
              }}
                onMouseOver={e => { e.target.style.background = '#FF6B35'; e.target.style.color = '#E5E7EB'; }}
                onMouseOut={e => { e.target.style.background = '#0057D9'; e.target.style.color = '#E5E7EB'; }}
              >Login</Link>
              <Link to='/register' style={{
                background: '#FF6B35',
                color: '#E5E7EB',
                padding: '0.4rem 1.1rem',
                borderRadius: '1.2rem',
                fontWeight: 700,
                textDecoration: 'none',
                border: '2px solid #FF6B35',
                transition: 'background 0.2s, color 0.2s',
              }}
                onMouseOver={e => { e.target.style.background = '#0057D9'; e.target.style.color = '#E5E7EB'; }}
                onMouseOut={e => { e.target.style.background = '#FF6B35'; e.target.style.color = '#E5E7EB'; }}
              >Register</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
