import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  const { user, setUser } = useContext(UserContext);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setDropdownOpen(false);
    navigate('/login');
  };

  return (
    <>
      <nav className={`modern-navbar ${isScrolled ? 'scrolled' : ''}`}>
        <div className="container">
          <div className="navbar-content">
            {/* Logo */}
            <Link to="/" className="navbar-logo">
              <span className="logo-text">CUSTOM WEB</span>
            </Link>

            {/* Desktop Navigation */}
            <div className="navbar-nav desktop-nav">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`nav-link ${location.pathname === link.to ? 'active' : ''}`}
                >
                  {link.label}
                </Link>
              ))}
            </div>

            {/* Auth Buttons / User Dropdown */}
            <div className="navbar-auth desktop-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                  >
                    <span className="user-avatar">{user.name ? user.name[0].toUpperCase() : 'U'}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="dropdown-caret">▼</span>
                  </button>
                  {dropdownOpen && (
                    <div className="user-dropdown-menu" onMouseLeave={() => setDropdownOpen(false)}>
                      <Link to={user.role === 'admin' ? '/admin' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                      </Link>
                      {/* <Link to="/profile" className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        Profile
                      </Link> */}
                      <button className="dropdown-item logout-btn" onClick={handleLogout}>
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="auth-buttons">
                  <Link to='/login' className="btn btn-primary">
                    Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary">
                    Register
                  </Link>
                </div>
              )}
            </div>

            {/* Mobile Menu Button */}
            <button 
              className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={toggleMobileMenu}
              aria-label="Toggle mobile menu"
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </div>
        </div>

        {/* Mobile Menu Overlay */}
        <div className={`mobile-menu-overlay ${isMobileMenuOpen ? 'active' : ''}`}>
          <div className="mobile-menu-content">
            <div className="mobile-nav-links">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`mobile-nav-link ${location.pathname === link.to ? 'active' : ''}`}
                >
                  {link.label}
                </Link>
              ))}
            </div>
            <div className="mobile-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                  >
                    <span className="user-avatar">{user.name ? user.name[0].toUpperCase() : 'U'}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="dropdown-caret">▼</span>
                  </button>
                  {dropdownOpen && (
                    <div className="user-dropdown-menu" onMouseLeave={() => setDropdownOpen(false)}>
                      <Link to={user.role === 'admin' ? '/admin' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                      </Link>
                      {/* <Link to="/profile" className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        Profile
                      </Link> */}
                      <button className="dropdown-item logout-btn" onClick={handleLogout}>
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="mobile-auth-buttons">
                  <Link to='/login' className="btn btn-primary">
                    Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary">
                    Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Navbar Styles */}
      <style jsx>{`
        .modern-navbar {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: rgba(35, 39, 47, 0.97);
          backdrop-filter: blur(10px);
          border-bottom: 1.5px solid rgba(255, 255, 255, 0.13);
          z-index: 1000;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          min-height: 48px;
          box-shadow: 0 2px 12px rgba(0,0,0,0.10);
        }
        .modern-navbar.scrolled {
          background: rgba(35, 39, 47, 0.99);
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.18);
        }
        .navbar-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 0.5rem 0;
          min-height: 48px;
        }
        .navbar-logo {
          text-decoration: none;
          z-index: 1001;
          margin-right: 2.2rem;
        }
        .logo-text {
          font-weight: 800;
          font-size: clamp(1.1rem, 2.5vw, 1.3rem);
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          letter-spacing: 1px;
        }
        .navbar-nav {
          display: flex;
          align-items: center;
          gap: clamp(1.2rem, 3vw, 2.2rem);
        }
        .nav-link {
          color: #E5E7EB;
          text-decoration: none;
          font-weight: 500;
          font-size: clamp(0.85rem, 2vw, 0.95rem);
          padding: 0.3rem 0;
          position: relative;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .nav-link.active,
        .nav-link:hover {
          color: #2ECC71;
        }
        .nav-link.active::after,
        .nav-link:hover::after {
          width: 100%;
        }
        .nav-link::after {
          content: '';
          position: absolute;
          bottom: -2px;
          left: 0;
          width: 0;
          height: 2px;
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          transition: width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .navbar-auth {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .user-dropdown-wrapper {
          position: relative;
          display: inline-block;
        }
        .user-btn.user-dropdown-toggle {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background: none;
          border: none;
          color: #E5E7EB;
          font-weight: 600;
          font-size: 1rem;
          cursor: pointer;
          padding: 0.4rem 1rem 0.4rem 0.7rem;
          border-radius: 2rem;
          transition: background 0.2s;
        }
        .user-btn.user-dropdown-toggle:hover, .user-btn.user-dropdown-toggle:focus {
          background: rgba(46,204,113,0.10);
        }
        .user-avatar {
          width: 2.3rem;
          height: 2.3rem;
          background: linear-gradient(135deg, #2ECC71, #0057D9 80%);
          color: #fff;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.2rem;
          font-weight: 700;
          box-shadow: 0 2px 8px rgba(46,204,113,0.13), 0 1px 4px rgba(0,87,217,0.10);
          border: 2.5px solid #181A20;
        }
        .user-name {
          font-weight: 700;
          color: #E5E7EB;
          letter-spacing: 0.2px;
        }
        .dropdown-caret {
          font-size: 0.8rem;
          margin-left: 0.2rem;
        }
        .user-dropdown-menu {
          position: absolute;
          top: 110%;
          right: 0;
          min-width: 180px;
          background: #23272F;
          border-radius: 1rem;
          box-shadow: 0 8px 32px rgba(0,0,0,0.18);
          padding: 0.5rem 0;
          z-index: 2000;
          animation: dropdownFadeIn 0.2s;
        }
        @keyframes dropdownFadeIn {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .dropdown-item {
          display: block;
          width: 100%;
          padding: 0.7rem 1.2rem;
          color: #E5E7EB;
          background: none;
          border: none;
          text-align: left;
          font-size: 1rem;
          font-weight: 500;
          cursor: pointer;
          transition: background 0.18s;
          text-decoration: none;
        }
        .dropdown-item:hover, .dropdown-item:focus {
          background: rgba(46,204,113,0.10);
          color: #2ECC71;
        }
        .logout-btn {
          color: #FF6B35;
        }
        @media (max-width: 900px) {
          .navbar-content {
            padding: 0.3rem 0.5rem;
          }
          .navbar-nav {
            gap: 1rem;
          }
        }
        /* Mobile Styles */
        @media (max-width: 700px) {
          .modern-navbar {
            min-height: 44px;
          }
          .navbar-content {
            padding: 0.2rem 0.2rem;
          }
          .navbar-logo {
            margin-right: 1rem;
          }
        }
        .mobile-menu-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          width: 100vw;
          height: 100vh;
          background: rgba(24,26,32,0.98);
          z-index: 3000;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: flex-start;
          opacity: 0;
          pointer-events: none;
          transition: opacity 0.25s;
        }
        .mobile-menu-overlay.active {
          opacity: 1;
          pointer-events: auto;
        }
        .mobile-menu-content {
          width: 100vw;
          padding: 2.5rem 1.2rem 1.2rem 1.2rem;
          display: flex;
          flex-direction: column;
          align-items: center;
          animation: slideDown 0.25s;
        }
        @keyframes slideDown {
          from { transform: translateY(-30px); opacity: 0; }
          to { transform: translateY(0); opacity: 1; }
        }
        .mobile-nav-links {
          width: 100%;
          display: flex;
          flex-direction: column;
          gap: 1.2rem;
          margin-bottom: 2rem;
        }
        .mobile-nav-link {
          color: #E5E7EB;
          font-size: 1.1rem;
          font-weight: 600;
          text-decoration: none;
          padding: 0.7rem 0.5rem;
          border-radius: 0.7rem;
          text-align: center;
          transition: background 0.18s, color 0.18s;
        }
        .mobile-nav-link.active, .mobile-nav-link:hover {
          background: rgba(46,204,113,0.10);
          color: #2ECC71;
        }
        .mobile-auth {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .user-dropdown-wrapper {
          width: 100%;
        }
        .user-btn.user-dropdown-toggle {
          width: 100%;
          justify-content: center;
        }
        .user-dropdown-menu {
          left: 50%;
          right: auto;
          transform: translateX(-50%);
          min-width: 200px;
        }
      `}</style>
    </>
  );
}

export default Navbar;
