import React from 'react';
import { NavLink } from 'react-router-dom';

const navStyle = ({ isActive }) => ({
  color: isActive ? '#2ECC71' : '#E5E7EB',
  fontWeight: 600,
  textDecoration: 'none',
  padding: '0.7rem 1rem',
  borderRadius: '0.5rem',
  background: isActive ? '#181A20' : 'none',
  display: 'flex',
  alignItems: 'center',
  gap: '0.7rem'
});

const icon = txt => <span style={{ fontWeight: 700, fontSize: '1.1rem' }}>{txt}</span>;

const AdminSidebar = ({ onLogout }) => (
  <div style={{ width: '250px', background: '#181A20', minHeight: '100vh', padding: '2rem 1rem', color: '#E5E7EB', position: 'sticky', top: 0, overflowY: 'auto', maxHeight: '100vh', borderRight: '2px solid #23272F' }}>
    <div style={{ fontWeight: 700, fontSize: '1.5rem', color: '#2ECC71', marginBottom: '2rem', letterSpacing: '1px' }}>Admin</div>
    <nav style={{ display: 'flex', flexDirection: 'column', gap: '1.2rem' }}>
      <NavLink to="/admin-dashboard/home" style={navStyle}>{icon('üè†')} Home</NavLink>
      <NavLink to="/admin-dashboard/approve" style={navStyle}>{icon('‚úÖ')} Approve Subs</NavLink>
      <NavLink to="/admin-dashboard/user-plans" style={navStyle}>{icon('üë•')} User Plans</NavLink>
      <NavLink to="/admin-dashboard/users" style={navStyle}>{icon('üßë‚Äçüíº')} Manage Users</NavLink>
      <NavLink to="/admin-dashboard/stats" style={navStyle}>{icon('üìä')} Site Stats</NavLink>
      <NavLink to="/admin-dashboard/notifications" style={navStyle}>{icon('üîî')} Notifications</NavLink>
      <NavLink to="/admin-dashboard/plans" style={navStyle}>{icon('üì¶')} Plans</NavLink>
      <NavLink to="/admin-dashboard/contacts" style={navStyle}>{icon('üì¨')} Contacts</NavLink>
      <NavLink to="/admin-dashboard/logout" style={navStyle}>{icon('üö™')} Logout</NavLink>
    </nav>
  </div>
);

export default AdminSidebar;
