import React, { useEffect, useState } from 'react';

const mockNotifications = [
  { id: 1, title: 'Welcome!', message: 'Thanks for joining. Explore your dashboard.', read: false, date: new Date() },
  { id: 2, title: 'Subscription Approved', message: 'Your Pro plan is now active.', read: true, date: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000) },
];

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  useEffect(() => {
    // Simulate fetch
    setTimeout(() => setNotifications(mockNotifications), 500);
  }, []);
  const unreadCount = notifications.filter(n => !n.read).length;
  return (
    <div style={{ maxWidth: 600, margin: '0 auto', color: '#E5E7EB' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>Notifications {unreadCount > 0 && <span style={{ background: '#FF6B35', color: '#fff', borderRadius: '1rem', padding: '0.2rem 0.8rem', fontSize: '1rem', marginLeft: '0.7rem' }}>{unreadCount}</span>}</h2>
      {notifications.length === 0 ? (
        <div style={{ color: '#A0AEC0', textAlign: 'center', marginTop: '2rem' }}>No notifications yet.</div>
      ) : (
        notifications.map(n => (
          <div key={n.id} style={{ background: n.read ? '#23272F' : '#2ECC71', color: n.read ? '#E5E7EB' : '#181A20', borderRadius: '1rem', padding: '1.2rem', marginBottom: '1rem', boxShadow: '0 1px 6px rgba(0,0,0,0.07)' }}>
            <div style={{ fontWeight: 700, fontSize: '1.1rem', marginBottom: '0.5rem' }}>{n.title}</div>
            <div style={{ marginBottom: '0.5rem' }}>{n.message}</div>
            <div style={{ fontSize: '0.9rem', color: n.read ? '#A0AEC0' : '#181A20' }}>{n.date.toLocaleDateString()}</div>
          </div>
        ))
      )}
    </div>
  );
};

export default Notifications;
