import React, { useEffect, useState } from 'react';
import axios from 'axios';

const planDisplayNames = {
  starter: 'Starter',
  premium: 'Premium',
  pro: 'Pro',
};

const MyPurchases = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [requestingRenewal, setRequestingRenewal] = useState(null);

  useEffect(() => {
    const fetchSubscriptions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-subscriptions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubscriptions(res.data.subscriptions);
      } catch (err) {
        setError('Could not fetch purchases.');
      }
      setLoading(false);
    };
    fetchSubscriptions();
  }, []);

  const handleRenewalRequest = async (subscriptionId) => {
    setRequestingRenewal(subscriptionId);
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/auth/renewal-request', { subscriptionId }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Refresh the subscriptions to show updated status
      const res = await axios.get('/api/auth/user-subscriptions', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSubscriptions(res.data.subscriptions);
    } catch (err) {
      setError(err.response?.data?.message || 'Could not submit renewal request.');
    }
    setRequestingRenewal(null);
  };

  const getRenewalStatusColor = (status) => {
    switch (status) {
      case 'approved': return '#2ECC71';
      case 'rejected': return '#FF6B35';
      case 'pending': return '#FFA500';
      default: return '#9CA3AF';
    }
  };

  const getRenewalStatusText = (status) => {
    switch (status) {
      case 'approved': return 'Approved';
      case 'rejected': return 'Rejected';
      case 'pending': return 'Pending';
      default: return 'None';
    }
  };

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>My Purchases</h2>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
        {loading ? (
          <div>Loading...</div>
        ) : error ? (
          <div style={{ color: '#FF6B35' }}>{error}</div>
        ) : subscriptions.length === 0 ? (
          <div>No purchases found.</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {subscriptions.map(sub => {
              const isExpired = sub.expiresAt && new Date(sub.expiresAt) < new Date();
              const canRequestRenewal = (sub.status === 'active' || sub.status === 'expired') && !sub.renewalRequested;
              const hasRenewalRequest = sub.renewalRequested && sub.renewalStatus !== 'none';
              
              return (
                <li key={sub._id} style={{ marginBottom: '1.5rem', background: '#181A20', borderRadius: '0.5rem', padding: '1.5rem', position: 'relative' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '1rem' }}>
                    <div>
                      <div><b>Plan:</b> {planDisplayNames[sub.plan]}</div>
                      <div><b>Subscription ID:</b> {sub.uniqueId}</div>
                      <div><b>Status:</b> <span style={{ color: sub.status === 'active' ? '#2ECC71' : '#FF6B35', fontWeight: 700 }}>{sub.status}</span></div>
                      <div style={{ fontSize: '0.95rem', color: '#A0AEC0' }}>Created: {new Date(sub.createdAt).toLocaleString()}</div>
                      {sub.expiresAt && (
                        <div style={{ fontSize: '0.95rem', color: '#A0AEC0' }}>
                          Expires: {new Date(sub.expiresAt).toLocaleString()}
                        </div>
                      )}
                    </div>
                    {isExpired && (
                      <span style={{ 
                        background: '#FF6B35', 
                        color: '#fff', 
                        borderRadius: '0.7rem', 
                        padding: '0.2rem 0.8rem', 
                        fontWeight: 700, 
                        fontSize: '0.95rem' 
                      }}>
                        Expired
                      </span>
                    )}
                  </div>

                  {/* Renewal Request Section */}
                  {hasRenewalRequest && (
                    <div style={{ 
                      background: 'rgba(255, 165, 0, 0.1)', 
                      borderRadius: '0.5rem', 
                      padding: '1rem', 
                      marginTop: '1rem',
                      border: '1px solid rgba(255, 165, 0, 0.3)'
                    }}>
                      <div style={{ marginBottom: '0.5rem' }}>
                        <b>Renewal Status:</b> 
                        <span style={{ 
                          color: getRenewalStatusColor(sub.renewalStatus), 
                          fontWeight: 700,
                          marginLeft: '0.5rem'
                        }}>
                          {getRenewalStatusText(sub.renewalStatus)}
                        </span>
                      </div>
                      {sub.renewalRequestDate && (
                        <div style={{ fontSize: '0.9rem', color: '#A0AEC0' }}>
                          Requested: {new Date(sub.renewalRequestDate).toLocaleString()}
                        </div>
                      )}
                      {sub.renewalStatus === 'approved' && sub.renewalExpiresAt && (
                        <div style={{ fontSize: '0.9rem', color: '#2ECC71' }}>
                          New expiry: {new Date(sub.renewalExpiresAt).toLocaleString()}
                        </div>
                      )}
                      {sub.renewalStatus === 'rejected' && sub.renewalRejectionReason && (
                        <div style={{ fontSize: '0.9rem', color: '#FF6B35', marginTop: '0.5rem' }}>
                          <b>Reason:</b> {sub.renewalRejectionReason}
                        </div>
                      )}
                    </div>
                  )}

                  {/* Renewal Request Button */}
                  {canRequestRenewal && (
                    <div style={{ marginTop: '1rem' }}>
                      <button
                        onClick={() => handleRenewalRequest(sub._id)}
                        disabled={requestingRenewal === sub._id}
                        style={{
                          background: '#2ECC71',
                          color: '#181A20',
                          border: 'none',
                          borderRadius: '0.5rem',
                          padding: '0.7rem 1.2rem',
                          fontWeight: 600,
                          cursor: requestingRenewal === sub._id ? 'not-allowed' : 'pointer',
                          opacity: requestingRenewal === sub._id ? 0.7 : 1
                        }}
                      >
                        {requestingRenewal === sub._id ? 'Requesting...' : 'Request Renewal'}
                      </button>
                    </div>
                  )}
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
};

export default MyPurchases;
