import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [markingRead, setMarkingRead] = useState(null);

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/auth/user-notifications', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotifications(res.data.notifications || []);
    } catch (err) {
      setError('Could not fetch notifications.');
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (notificationId) => {
    if (markingRead === notificationId) return;
    
    setMarkingRead(notificationId);
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/auth/notifications/read/${notificationId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Update local state
      setNotifications(prev => prev.map(n => 
        n._id === notificationId ? { ...n, read: true } : n
      ));
    } catch (err) {
      setError('Could not mark notification as read.');
    } finally {
      setMarkingRead(null);
    }
  };

  const markAllAsRead = async () => {
    try {
      const token = localStorage.getItem('token');
      const unreadNotifications = notifications.filter(n => !n.read);
      
      // Mark all unread notifications as read
      await Promise.all(
        unreadNotifications.map(n => 
          axios.post(`/api/auth/notifications/read/${n._id}`, {}, {
            headers: { Authorization: `Bearer ${token}` }
          })
        )
      );
      
      // Update local state
      setNotifications(prev => prev.map(n => ({ ...n, read: true })));
    } catch (err) {
      setError('Could not mark all notifications as read.');
    }
  };

  const getNotificationIcon = (title) => {
    if (title?.toLowerCase().includes('welcome')) return 'ðŸ‘‹';
    if (title?.toLowerCase().includes('subscription') || title?.toLowerCase().includes('plan')) return 'ðŸ“¦';
    if (title?.toLowerCase().includes('payment')) return 'ðŸ’³';
    if (title?.toLowerCase().includes('renewal')) return 'ðŸ”„';
    if (title?.toLowerCase().includes('expired') || title?.toLowerCase().includes('expiry')) return 'âš ï¸';
    if (title?.toLowerCase().includes('approved')) return 'âœ…';
    if (title?.toLowerCase().includes('rejected')) return 'âŒ';
    return 'ðŸ””';
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${diffInHours} hour${diffInHours > 1 ? 's' : ''} ago`;
    if (diffInHours < 48) return 'Yesterday';
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  if (loading) {
    return (
      <div style={{ 
        maxWidth: 800, 
        margin: '0 auto', 
        color: '#E5E7EB',
        textAlign: 'center',
        padding: '2rem'
      }}>
        <div style={{
          width: 40,
          height: 40,
          border: '3px solid rgba(46, 204, 113, 0.3)',
          borderTop: '3px solid #2ECC71',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite',
          margin: '0 auto 1rem'
        }}></div>
        <p>Loading notifications...</p>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 800, margin: '0 auto', color: '#E5E7EB', padding: '0 1rem' }}>
      {/* Header */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '1.5rem',
        flexWrap: 'wrap',
        gap: '1rem'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <h2 style={{ 
            color: '#2ECC71', 
            fontWeight: 700, 
            fontSize: '1.7rem', 
            margin: 0 
          }}>
            Notifications
          </h2>
          {unreadCount > 0 && (
            <span style={{ 
              background: '#FF6B35', 
              color: '#fff', 
              borderRadius: '1rem', 
              padding: '0.2rem 0.8rem', 
              fontSize: '0.9rem', 
              fontWeight: 600
            }}>
              {unreadCount} new
            </span>
          )}
        </div>
        
        {unreadCount > 0 && (
          <button
            onClick={markAllAsRead}
            style={{
              background: '#2ECC71',
              color: '#181A20',
              border: 'none',
              borderRadius: '0.5rem',
              padding: '0.5rem 1rem',
              fontWeight: 600,
              cursor: 'pointer',
              fontSize: '0.9rem'
            }}
          >
            Mark all as read
          </button>
        )}
      </div>

      {/* Error Message */}
      {error && (
        <div style={{ 
          background: 'rgba(255, 107, 53, 0.1)', 
          border: '1px solid #FF6B35',
          color: '#FF6B35', 
          borderRadius: '0.5rem', 
          padding: '1rem', 
          marginBottom: '1rem' 
        }}>
          {error}
        </div>
      )}

      {/* Notifications List */}
      {notifications.length === 0 ? (
        <div style={{ 
          background: '#23272F', 
          borderRadius: '1rem', 
          padding: '3rem 2rem', 
          textAlign: 'center',
          color: '#9CA3AF'
        }}>
          <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ðŸ””</div>
          <h3 style={{ color: '#E5E7EB', marginBottom: '0.5rem' }}>No notifications yet</h3>
          <p>You'll see important updates and announcements here.</p>
        </div>
      ) : (
        <div style={{ display: 'grid', gap: '1rem' }}>
          {notifications.map(notification => (
            <div 
              key={notification._id} 
              style={{ 
                background: notification.read ? '#23272F' : 'rgba(46, 204, 113, 0.1)', 
                color: notification.read ? '#E5E7EB' : '#E5E7EB', 
                borderRadius: '1rem', 
                padding: '1.5rem', 
                border: notification.read ? '1px solid #333' : '1px solid rgba(46, 204, 113, 0.3)',
                boxShadow: notification.read ? '0 1px 6px rgba(0,0,0,0.07)' : '0 2px 12px rgba(46, 204, 113, 0.1)',
                transition: 'all 0.3s ease',
                position: 'relative'
              }}
            >
              {/* Unread indicator */}
              {!notification.read && (
                <div style={{
                  position: 'absolute',
                  top: '1rem',
                  right: '1rem',
                  width: '8px',
                  height: '8px',
                  background: '#2ECC71',
                  borderRadius: '50%'
                }}></div>
              )}

              <div style={{ display: 'flex', gap: '1rem', alignItems: 'flex-start' }}>
                {/* Icon */}
                <div style={{ 
                  fontSize: '1.5rem', 
                  marginTop: '0.2rem',
                  opacity: notification.read ? 0.7 : 1
                }}>
                  {getNotificationIcon(notification.title)}
                </div>

                {/* Content */}
                <div style={{ flex: 1 }}>
                  <div style={{ 
                    fontWeight: 700, 
                    fontSize: '1.1rem', 
                    marginBottom: '0.5rem',
                    color: notification.read ? '#E5E7EB' : '#2ECC71'
                  }}>
                    {notification.title || 'Notification'}
                  </div>
                  <div style={{ 
                    marginBottom: '1rem',
                    lineHeight: '1.5',
                    color: notification.read ? '#A0AEC0' : '#E5E7EB'
                  }}>
                    {notification.message}
                  </div>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    flexWrap: 'wrap',
                    gap: '0.5rem'
                  }}>
                    <div style={{ 
                      fontSize: '0.9rem', 
                      color: notification.read ? '#6B7280' : '#9CA3AF'
                    }}>
                      {formatDate(notification.createdAt)}
                    </div>
                    
                    {!notification.read && (
                      <button
                        onClick={() => markAsRead(notification._id)}
                        disabled={markingRead === notification._id}
                        style={{
                          background: 'transparent',
                          color: '#2ECC71',
                          border: '1px solid #2ECC71',
                          borderRadius: '0.5rem',
                          padding: '0.3rem 0.8rem',
                          fontWeight: 600,
                          cursor: markingRead === notification._id ? 'not-allowed' : 'pointer',
                          fontSize: '0.8rem',
                          opacity: markingRead === notification._id ? 0.7 : 1,
                          transition: 'all 0.2s ease'
                        }}
                        onMouseOver={(e) => {
                          if (markingRead !== notification._id) {
                            e.target.style.background = '#2ECC71';
                            e.target.style.color = '#181A20';
                          }
                        }}
                        onMouseOut={(e) => {
                          if (markingRead !== notification._id) {
                            e.target.style.background = 'transparent';
                            e.target.style.color = '#2ECC71';
                          }
                        }}
                      >
                        {markingRead === notification._id ? 'Marking...' : 'Mark as read'}
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

