import React, { useEffect, useState } from 'react';
import { useOutletContext, Link } from 'react-router-dom';
import axios from 'axios';

const planDisplayNames = {
  starter: 'Starter',
  premium: 'Premium',
  pro: 'Pro',
};

const DashboardHome = () => {
  const { user } = useOutletContext();
  const [subs, setSubs] = useState([]);
  const [subsLoading, setSubsLoading] = useState(true);
  const [subsError, setSubsError] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [notifLoading, setNotifLoading] = useState(true);
  const [notifError, setNotifError] = useState('');

  useEffect(() => {
    // Fetch subscriptions
    const fetchSubs = async () => {
      setSubsLoading(true);
      setSubsError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-subscriptions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubs(res.data.subscriptions || []);
      } catch (err) {
        setSubsError('Could not fetch subscription info.');
      }
      setSubsLoading(false);
    };
    // Fetch notifications
    const fetchNotifs = async () => {
      setNotifLoading(true);
      setNotifError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-notifications', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNotifications(res.data.notifications || []);
      } catch (err) {
        setNotifError('Could not fetch notifications.');
      }
      setNotifLoading(false);
    };
    fetchSubs();
    fetchNotifs();
  }, []);

  // Current subscription logic
  let current = null;
  if (subs.length > 0) {
    current = subs.find(s => s.status === 'active' || s.status === 'pending') || subs[0];
  }
  const daysLeft = current && current.expiresAt ? Math.ceil((new Date(current.expiresAt) - Date.now()) / (1000 * 60 * 60 * 24)) : null;
  const isExpired = current && current.expiresAt && new Date(current.expiresAt) < new Date();

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '900px', margin: '0 auto' }}>
      <h1 style={{ color: '#2ECC71', fontWeight: 800, fontSize: '2.4rem', marginBottom: '1.2rem', textAlign: 'center' }}>
        Welcome back, {user?.name || 'User'}!
      </h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '2.5rem', marginBottom: '2.5rem', flexWrap: 'wrap' }}>
        <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.2rem 2rem', minWidth: '200px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
          <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71', marginBottom: '0.3rem' }}>Your Role</div>
          <div style={{ fontSize: '1.1rem' }}>{user?.role}</div>
        </div>
        <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.2rem 2rem', minWidth: '200px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
          <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71', marginBottom: '0.3rem' }}>Email</div>
          <div style={{ fontSize: '1.1rem' }}>{user?.email}</div>
        </div>
        <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.2rem 2rem', minWidth: '200px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
          <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71', marginBottom: '0.3rem' }}>Joined</div>
          <div style={{ fontSize: '1.1rem' }}>{user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : ''}</div>
        </div>
      </div>
      <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap', justifyContent: 'center', marginBottom: '2.5rem' }}>
        {/* Subscription Widget */}
        <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', minWidth: 260, maxWidth: 340, flex: 1, boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
          <div style={{ fontWeight: 700, fontSize: '1.2rem', color: '#2ECC71', marginBottom: '0.7rem' }}>Current Plan</div>
          {subsLoading ? <div style={{ color: '#A0AEC0' }}>Loading...</div> : subsError ? <div style={{ color: '#FF6B35' }}>{subsError}</div> : !current ? <div>No active plan.</div> : (
            <>
              <div style={{ fontSize: '1.1rem', marginBottom: '0.5rem' }}><b>Plan:</b> {planDisplayNames[current.plan]}</div>
              <div style={{ fontSize: '1.1rem', marginBottom: '0.5rem' }}><b>Status:</b> <span style={{ color: current.status === 'active' ? '#2ECC71' : '#FF6B35', fontWeight: 700 }}>{current.status}</span></div>
              <div style={{ fontSize: '1.1rem', marginBottom: '0.5rem' }}><b>Expires:</b> {current.expiresAt ? new Date(current.expiresAt).toLocaleDateString() : 'N/A'}</div>
              {daysLeft !== null && !isExpired && <div style={{ color: '#FFD700', fontWeight: 600 }}>Days left: {daysLeft}</div>}
              {isExpired && <div style={{ color: '#FF6B35', fontWeight: 700 }}>Expired</div>}
              <Link to="/dashboard/subscription" style={{ color: '#2ECC71', textDecoration: 'underline', fontWeight: 700, display: 'block', marginTop: '0.7rem' }}>Manage</Link>
            </>
          )}
        </div>
        {/* Notifications Widget */}
        <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', minWidth: 260, maxWidth: 340, flex: 1, boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
          <div style={{ fontWeight: 700, fontSize: '1.2rem', color: '#2ECC71', marginBottom: '0.7rem' }}>Recent Notifications</div>
          {notifLoading ? <div style={{ color: '#A0AEC0' }}>Loading...</div> : notifError ? <div style={{ color: '#FF6B35' }}>{notifError}</div> : notifications.length === 0 ? <div>No notifications.</div> : (
            notifications.slice(0, 2).map(n => (
              <div key={n._id} style={{ background: n.read ? '#23272F' : '#2ECC71', color: n.read ? '#E5E7EB' : '#181A20', borderRadius: '0.7rem', padding: '0.8rem', marginBottom: '0.7rem', boxShadow: '0 1px 6px rgba(0,0,0,0.07)' }}>
                <div style={{ fontWeight: 700, fontSize: '1.05rem', marginBottom: '0.3rem' }}>{n.title || 'Notification'}</div>
                <div style={{ marginBottom: '0.3rem' }}>{n.message}</div>
                <div style={{ fontSize: '0.95rem', color: n.read ? '#A0AEC0' : '#181A20' }}>{new Date(n.createdAt).toLocaleDateString()}</div>
              </div>
            ))
          )}
          <Link to="/dashboard/notifications" style={{ color: '#2ECC71', textDecoration: 'underline', fontWeight: 700, display: 'block', marginTop: '0.7rem' }}>View All</Link>
        </div>
      </div>
      {/* Quick Links */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '2rem', flexWrap: 'wrap' }}>
        <Link to="/dashboard/subscription" style={{ textDecoration: 'none' }}>
          <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.5rem 2rem', minWidth: '180px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)', transition: 'box-shadow 0.2s', cursor: 'pointer' }}>
            <div style={{ fontSize: '2.2rem', marginBottom: '0.5rem' }}>💳</div>
            <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71' }}>Subscription</div>
          </div>
        </Link>
        <Link to="/dashboard/purchases" style={{ textDecoration: 'none' }}>
          <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.5rem 2rem', minWidth: '180px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)', transition: 'box-shadow 0.2s', cursor: 'pointer' }}>
            <div style={{ fontSize: '2.2rem', marginBottom: '0.5rem' }}>🛒</div>
            <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71' }}>My Purchases</div>
          </div>
        </Link>
        <Link to="/dashboard/support" style={{ textDecoration: 'none' }}>
          <div style={{ background: '#23272F', borderRadius: '1rem', padding: '1.5rem 2rem', minWidth: '180px', textAlign: 'center', boxShadow: '0 2px 12px rgba(0,0,0,0.08)', transition: 'box-shadow 0.2s', cursor: 'pointer' }}>
            <div style={{ fontSize: '2.2rem', marginBottom: '0.5rem' }}>💬</div>
            <div style={{ fontWeight: 700, fontSize: '1.1rem', color: '#2ECC71' }}>Support</div>
          </div>
        </Link>
      </div>
    </div>
  );
};

export default DashboardHome;
