import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    axios.get('/api/dashboard', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setUser(res.data.user);
        setLoading(false);
      })
      .catch(() => {
        localStorage.removeItem('token');
        navigate('/login');
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) return <div style={{ color: '#E5E7EB', textAlign: 'center', marginTop: '4rem' }}>Loading dashboard...</div>;

  return (
    <div style={{ background: '#181A20', minHeight: '100vh', padding: '2rem 0' }}>
      <section className="responsive-section card-bg" style={{
        maxWidth: '500px',
        margin: '3rem auto',
        padding: '2.5rem 2rem',
        borderRadius: '1.5rem',
        boxShadow: '0 2px 16px rgba(0,0,0,0.10)',
        color: '#E5E7EB',
        textAlign: 'center',
      }}>
        <h1 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '2rem', marginBottom: '1.2rem' }}>User Dashboard</h1>
        <div style={{ marginBottom: '1.5rem' }}>
          <div><b>Name:</b> {user.name}</div>
          <div><b>Email:</b> {user.email}</div>
          <div><b>Role:</b> {user.role}</div>
        </div>
        <button onClick={handleLogout} style={{
          background: '#FF6B35',
          color: '#E5E7EB',
          border: 'none',
          borderRadius: '2rem',
          padding: '0.7rem 2rem',
          fontWeight: 700,
          fontSize: '1.1rem',
          cursor: 'pointer',
        }}>Logout</button>
      </section>
    </div>
  );
}

export default Dashboard;
