import React from 'react';
import { NavLink } from 'react-router-dom';
import './Sidebar.css';

const navItems = [
  { to: '/dashboard', label: 'Dashboard', icon: 'üè†' },
  { to: '/dashboard/support', label: 'Help & Support', icon: 'üí¨' },
  { to: '/dashboard/subscription', label: 'Subscription', icon: 'üì¶' },
  { to: '/dashboard/purchases', label: 'My Purchases', icon: 'üõí' },
  { to: '/dashboard/notifications', label: 'Notifications', icon: 'üîî' },
];

// Mobile bottom nav: exactly 5 items as requested
const mobileNavItems = [
  { to: '/dashboard', label: 'Home', icon: 'üè†' },
  { to: '/dashboard/support', label: 'Help', icon: 'üí¨' },
  { to: '/dashboard/subscription', label: 'Subscription', icon: 'üì¶' },
  { to: '/dashboard/purchases', label: 'Purchase', icon: 'üõí' },
  // Fifth item will be Logout button (üö™)
];

const Sidebar = ({ onLogout, isPlanExpired, user }) => (
  <>
    <div className="modern-sidebar">
      {/* Sidebar Header */}
      <div className="sidebar-header">
        <div className="user-avatar">
          <span>üë§</span>
        </div>
        <div className="sidebar-title">
          <h2>User Panel</h2>
          <p>Welcome back!</p>
        </div>
      </div>

      {/* Navigation */}
      <nav className="sidebar-nav">
        {navItems.map(item => (
          <NavLink
            key={item.to}
            to={item.to}
            className={({ isActive }) => `nav-item${isActive ? ' active' : ''}`}
            tabIndex={0}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
            <span className="nav-indicator"></span>
          </NavLink>
        ))}
        {isPlanExpired && (
          <NavLink
            to="/dashboard/expired"
            className={({ isActive }) => `nav-item expired${isActive ? ' active' : ''}`}
            tabIndex={0}
          >
            <span className="nav-icon">‚è∞</span>
            <span className="nav-label">Plan Expired</span>
            <span className="nav-indicator"></span>
          </NavLink>
        )}
      </nav>

      {/* Logout Button */}
      <button onClick={onLogout} className="logout-btn" tabIndex={0}>
        <span className="logout-icon">üö™</span>
        <span>Logout</span>
      </button>
    </div>

    {/* Mobile Bottom Nav - only when logged in */}
    {user && (
    <nav className="mobile-bottom-nav">
      {mobileNavItems.map(item => (
        <NavLink
          key={item.to}
          to={item.to}
          end
          className={({ isActive }) => `bottom-nav-item${isActive ? ' active' : ''}`}
          tabIndex={0}
        >
          <span className="bottom-nav-icon">{item.icon}</span>
          <span className="bottom-nav-label">{item.label}</span>
        </NavLink>
      ))}
      <button className="bottom-nav-item logout" onClick={onLogout} tabIndex={0}>
        <span className="bottom-nav-icon">üö™</span>
        <span className="bottom-nav-label">Logout</span>
      </button>
    </nav>
    )}
  </>
);

export default Sidebar;
