import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';
import axios from '../axios';
import './Plans.css';

function Plans() {
  const navigate = useNavigate();
  const { user } = React.useContext(UserContext);
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);

  useEffect(() => {
    const fetchPlans = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await axios.get('/api/auth/plans');
        setPlans(res.data.plans);
      } catch (err) {
        setError('Could not fetch plans.');
      }
      setLoading(false);
    };
    fetchPlans();
    setIsVisible(true);
  }, []);

  const handleBuy = (planName) => {
    if (!user) {
      navigate('/login');
    } else {
      // Clean the plan name for URL
      const cleanPlanName = planName.toLowerCase().replace(/\s+/g, '');
      navigate(`/payment/${cleanPlanName}`);
    }
  };

  const handlePlanSelect = (plan) => {
    setSelectedPlan(plan);
  };

  // Show last plan first
  const sortedPlans = [...plans].reverse();

  const planBenefits = [
    {
      icon: 'üöÄ',
      title: 'Fast Delivery',
      description: 'Get your website up and running quickly'
    },
    {
      icon: 'üîí',
      title: 'Secure & Reliable',
      description: 'Your data is safe with enterprise-grade security'
    },
    {
      icon: 'üì±',
      title: 'Mobile Responsive',
      description: 'Perfect display on all devices and screens'
    },
    {
      icon: 'üé®',
      title: 'Custom Design',
      description: 'Unique designs tailored to your brand'
    },
    {
      icon: '‚ö°',
      title: 'High Performance',
      description: 'Lightning-fast loading speeds and optimization'
    },
    {
      icon: 'üõ†Ô∏è',
      title: '24/7 Support',
      description: 'Round-the-clock technical support and maintenance'
    }
  ];

  const comparisonFeatures = [
    'Custom Domain',
    'SSL Certificate',
    'Mobile Responsive',
    'SEO Optimization',
    'Content Management',
    'E-commerce Features',
    'Analytics Integration',
    'Social Media Integration',
    'Email Marketing',
    'Priority Support',
    'Custom Development',
    'Performance Optimization'
  ];

  return (
    <div className="plans-page">
      {/* Hero Section */}
      <section className="plans-hero">
        <div className="hero-background">
          <div className="hero-gradient"></div>
          <div className="floating-shapes">
            <div className="shape shape-1"></div>
            <div className="shape shape-2"></div>
            <div className="shape shape-3"></div>
            <div className="shape shape-4"></div>
          </div>
        </div>

        <div className="container">
          <div className={`hero-content ${isVisible ? 'animate-in' : ''}`}>
            <div className="hero-text">
              <h1 className="hero-title">
                Choose Your <span className="gradient-text">Plan</span>
              </h1>
              <p className="hero-subtitle">
                Select the perfect plan that fits your needs and budget. All plans include premium features and 24/7 support.
              </p>
              <div className="hero-stats">
                <div className="stat-item" style={{ animationDelay: '0.1s' }}>
                  <div className="stat-icon">‚≠ê</div>
                  <span className="stat-number">100%</span>
                  <span className="stat-label">Satisfaction</span>
                </div>
                <div className="stat-item" style={{ animationDelay: '0.2s' }}>
                  <div className="stat-icon">üöÄ</div>
                  <span className="stat-number">24hrs</span>
                  <span className="stat-label">Delivery</span>
                </div>
                <div className="stat-item" style={{ animationDelay: '0.3s' }}>
                  <div className="stat-icon">üí¨</div>
                  <span className="stat-number">24/7</span>
                  <span className="stat-label">Support</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Benefits Section */}
      <section className="benefits-section section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Why Choose Our Plans?</h2>
            <p className="section-subtitle">
              Every plan comes with premium features and exceptional value
            </p>
          </div>
          <div className="benefits-grid">
            {planBenefits.map((benefit, index) => (
              <div key={benefit.title} className={`benefit-card ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: `${index * 0.1}s` }}>
                <div className="benefit-icon">{benefit.icon}</div>
                <h3 className="benefit-title">{benefit.title}</h3>
                <p className="benefit-description">{benefit.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Plans Section */}
      <section className="plans-section section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Our Pricing Plans</h2>
            <p className="section-subtitle">
              Choose the plan that best fits your business needs
            </p>
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>Loading plans...</p>
            </div>
          ) : error ? (
            <div className="error-container">
              <div className="error-icon">‚ö†Ô∏è</div>
              <h3>Oops! Something went wrong</h3>
              <p>{error}</p>
              <button onClick={() => window.location.reload()} className="btn btn-primary">
                Try Again
              </button>
            </div>
          ) : (
            <div className="plans-grid">
              {sortedPlans.map((plan, index) => (
                <div
                  key={plan._id}
                  className={`plan-card ${plan.highlight ? 'best-value' : ''} ${isVisible ? 'animate-in' : ''} ${selectedPlan?._id === plan._id ? 'selected' : ''}`}
                  style={{ animationDelay: `${index * 0.1}s` }}
                  onClick={() => handlePlanSelect(plan)}
                >
                  {plan.highlight && (
                    <div className="best-value-badge">
                      <span className="badge-icon">üèÜ</span>
                      <span>Best Value</span>
                    </div>
                  )}

                  <div className="plan-header">
                    <h2 className="plan-name">{plan.name}</h2>
                    <div className="plan-price-container">
                      <div className="plan-price">
                        ‚Çπ{plan.price}
                      </div>
                      {plan.oldPrice && (
                        <div className="plan-old-price">‚Çπ{plan.oldPrice}</div>
                      )}
                    </div>

                    {plan.oldPrice && parseFloat(plan.oldPrice) > parseFloat(plan.price) ? (
                      <div className="plan-save">
                        Save ~{Math.round(((parseFloat(plan.oldPrice) - parseFloat(plan.price)) / parseFloat(plan.oldPrice)) * 100)}%
                      </div>
                    ) : plan.save ? (
                      <div className="plan-save">Save ‚Çπ{plan.save}</div>
                    ) : null}

                    <div className="plan-duration">
                      <span className="duration-icon">‚è±Ô∏è</span>
                      {plan.duration} days
                    </div>
                  </div>

                  <div className="plan-features">
                    <h3 className="features-title">
                      <span className="features-icon">‚ú®</span>
                      What's Included:
                    </h3>
                    <ul className="features-list">
                      {plan.features.map((feature, index) => (
                        <li key={index} className="feature-item">
                          <span className="feature-check">‚úì</span>
                          <span className="feature-text">{feature}</span>
                        </li>
                      ))}
                    </ul>
                  </div>

                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleBuy(plan.name);
                    }}
                    className={`plan-button ${plan.highlight ? 'button-best' : ''}`}
                  >
                    <span>Get Started</span>
                    <span className="button-icon">‚Üí</span>
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Comparison Section */}
      <section className="comparison-section section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Plan Comparison</h2>
            <p className="section-subtitle">
              Compare features across all our plans
            </p>
          </div>

          <div className={`comparison-card ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: '0.4s' }}>
            <div className="comparison-table">
              <div className="comparison-header">
                <div className="feature-header">Features</div>
                {sortedPlans.map((plan, index) => (
                  <div key={plan._id} className={`plan-header-cell ${plan.highlight ? 'best-value' : ''}`}>
                    <h3>{plan.name}</h3>
                    <div className="plan-price">‚Çπ{plan.price}</div>
                  </div>
                ))}
              </div>

              <div className="comparison-body">
                {comparisonFeatures.map((feature, featureIndex) => (
                  <div key={feature} className="comparison-row">
                    <div className="feature-name">{feature}</div>
                    {sortedPlans.map((plan, planIndex) => (
                      <div key={plan._id} className={`feature-cell ${plan.highlight ? 'best-value' : ''}`}>
                        {plan.features.some(f => f.toLowerCase().includes(feature.toLowerCase())) ? (
                          <span className="feature-check">‚úì</span>
                        ) : (
                          <span className="feature-cross">‚úó</span>
                        )}
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* FAQ Section */}
      <section className="faq-section section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Frequently Asked Questions</h2>
            <p className="section-subtitle">
              Everything you need to know about our plans and services
            </p>
          </div>

          <div className="faq-grid">
            <div className={`faq-item ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: '0.1s' }}>
              <div className="faq-icon">‚ùì</div>
              <h3 className="faq-question">Can I upgrade my plan later?</h3>
              <p className="faq-answer">
                Yes! You can upgrade your plan at any time. The difference in price will be prorated based on your remaining subscription period.
              </p>
            </div>

            <div className={`faq-item ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: '0.2s' }}>
              <div className="faq-icon">üí≥</div>
              <h3 className="faq-question">What payment methods do you accept?</h3>
              <p className="faq-answer">
                We accept all major credit cards, debit cards, and digital wallets including PayPal, Google Pay, and Apple Pay.
              </p>
            </div>

            <div className={`faq-item ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: '0.3s' }}>
              <div className="faq-icon">üîÑ</div>
              <h3 className="faq-question">Is there a money-back guarantee?</h3>
              <p className="faq-answer">
                Yes, we offer a 30-day money-back guarantee. If you're not satisfied with our services, we'll refund your payment.
              </p>
            </div>

            <div className={`faq-item ${isVisible ? 'animate-in' : ''}`} style={{ animationDelay: '0.4s' }}>
              <div className="faq-icon">üéØ</div>
              <h3 className="faq-question">Do you offer custom plans?</h3>
              <p className="faq-answer">
                Absolutely! If none of our standard plans fit your needs, we can create a custom plan tailored to your specific requirements.
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="plans-cta section">
        <div className="container">
          <div className="cta-content">
            <h2 className="cta-title">Ready to Get Started?</h2>
            <p className="cta-description">
              Choose your plan and start building your dream website today. Our team is ready to help you succeed.
            </p>
            <div className="cta-actions">
              <button onClick={() => document.querySelector('.plans-grid').scrollIntoView({ behavior: 'smooth' })} className="btn btn-primary">
                <span>View All Plans</span>
                <span className="btn-icon">‚Üì</span>
              </button>
              <button onClick={() => navigate('/contact')} className="btn btn-outline">
                <span>Contact Us</span>
                <span className="btn-icon">üí¨</span>
              </button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Plans;
