import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';
import axios from 'axios';

function Plans() {
  const navigate = useNavigate();
  const { user } = React.useContext(UserContext);
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPlans = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await axios.get('/api/auth/plans');
        setPlans(res.data.plans);
      } catch (err) {
        setError('Could not fetch plans.');
      }
      setLoading(false);
    };
    fetchPlans();
  }, []);

  const handleBuy = (planName) => {
    if (!user) {
      navigate('/login');
    } else {
      navigate(`/payment/${planName.toLowerCase()}`);
    }
  };

  return (
    <div style={{ background: '#181A20', minHeight: '100vh', padding: '0 0 2rem 0' }}>
      <section className="responsive-section" style={{
        maxWidth: '1100px',
        margin: '0 auto',
        padding: '3rem 1rem',
      }}>
        <h1 style={{ color: '#0057D9', fontWeight: 800, fontSize: '2.5rem', marginBottom: '2.5rem', textAlign: 'center', letterSpacing: '1px' }}>
          Select Your Plan
        </h1>
        {loading ? (
          <div style={{ color: '#2ECC71', textAlign: 'center' }}>Loading plans...</div>
        ) : error ? (
          <div style={{ color: '#FF6B35', textAlign: 'center' }}>{error}</div>
        ) : (
          <div className="responsive-flex" style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem', justifyContent: 'center' }}>
            {plans.map((plan, idx) => (
              <div className="responsive-card" key={plan._id} style={{
                background: '#23272F',
                borderRadius: '1.5rem',
                boxShadow: plan.highlight ? '0 4px 24px rgba(46,204,113,0.15)' : '0 2px 12px rgba(0,0,0,0.06)',
                border: plan.highlight ? '2.5px solid #2ECC71' : '1.5px solid #23272F',
                minWidth: '270px',
                maxWidth: '320px',
                flex: '1 1 270px',
                padding: '2.5rem 2rem',
                textAlign: 'center',
                position: 'relative',
                zIndex: plan.highlight ? 2 : 1,
                transform: plan.highlight ? 'scale(1.05)' : 'none',
                color: '#E5E7EB',
              }}>
                {plan.highlight && (
                  <div style={{
                    position: 'absolute',
                    top: '-1.2rem',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    background: '#2ECC71',
                    color: '#181A20',
                    fontWeight: 700,
                    borderRadius: '1rem',
                    padding: '0.3rem 1.2rem',
                    fontSize: '1rem',
                    boxShadow: '0 2px 8px rgba(46,204,113,0.12)',
                  }}>
                    Best Value
                  </div>
                )}
                <h2 style={{ color: plan.color, fontWeight: 800, fontSize: '2rem', marginBottom: '0.5rem' }}>{plan.name}</h2>
                <div style={{ fontSize: '2.2rem', fontWeight: 800, color: plan.color, marginBottom: '0.2rem' }}>{plan.price}</div>
                <div style={{ color: '#A0AEC0', textDecoration: 'line-through', fontSize: '1.1rem', marginBottom: '0.2rem' }}>{plan.oldPrice}</div>
                <div style={{ color: '#FF6B35', fontWeight: 700, marginBottom: '1.2rem' }}>{plan.save}</div>
                <div style={{ color: '#A0AEC0', fontWeight: 600, marginBottom: '0.7rem' }}>Duration: {plan.duration} days</div>
                <ul style={{ color: '#E5E7EB', textAlign: 'left', margin: '0 auto 1.5rem auto', padding: 0, listStyle: 'none', maxWidth: '210px' }}>
                  {plan.features.map(f => (
                    <li key={f} style={{ marginBottom: '0.6rem', display: 'flex', alignItems: 'center' }}>
                      <span style={{ color: plan.color, fontWeight: 700, marginRight: '0.5rem' }}>â€¢</span> {f}
                    </li>
                  ))}
                </ul>
                <button
                  style={{
                    background: plan.color,
                    color: '#E5E7EB',
                    border: 'none',
                    borderRadius: '2rem',
                    padding: '0.8rem 2.2rem',
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    cursor: 'pointer',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.10)',
                    transition: 'background 0.2s',
                    border: `2px solid ${plan.color}`,
                  }}
                  onMouseOver={e => e.target.style.background = '#181A20'}
                  onMouseOut={e => e.target.style.background = plan.color}
                  onClick={() => handleBuy(plan.name)}
                >Buy Now</button>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Plans;
