import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  return (
    <nav style={{
      background: '#0057D9',
      color: '#fff',
      padding: '0.75rem 2rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      boxShadow: '0 2px 8px rgba(0,0,0,0.04)'
    }}>
      <div style={{ fontWeight: 700, fontSize: '1.5rem', letterSpacing: '1px' }}>
        CUSTOM WEB
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '2rem' }}>
        <div style={{ display: 'flex', gap: '1.5rem' }}>
          {navLinks.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={{
                color: '#fff',
                textDecoration: 'none',
                fontWeight: location.pathname === link.to ? 700 : 500,
                borderBottom: location.pathname === link.to ? '2px solid #FF6B35' : '2px solid transparent',
                padding: '0.25rem 0',
                transition: 'border 0.2s, color 0.2s',
              }}
              onMouseOver={e => e.target.style.color = '#FF6B35'}
              onMouseOut={e => e.target.style.color = '#fff'}
            >
              {link.label}
            </Link>
          ))}
        </div>
        <div style={{ height: '2rem', width: '1px', background: '#fff', margin: '0 1.5rem' }} />
        <div style={{ display: 'flex', gap: '1rem' }}>
          <Link to='/login' style={{
            background: '#fff',
            color: '#0057D9',
            padding: '0.4rem 1.1rem',
            borderRadius: '1.2rem',
            fontWeight: 700,
            textDecoration: 'none',
            border: '2px solid #fff',
            transition: 'background 0.2s, color 0.2s',
          }}
            onMouseOver={e => { e.target.style.background = '#FF6B35'; e.target.style.color = '#fff'; }}
            onMouseOut={e => { e.target.style.background = '#fff'; e.target.style.color = '#0057D9'; }}
          >Login</Link>
          <Link to='/register' style={{
            background: '#FF6B35',
            color: '#fff',
            padding: '0.4rem 1.1rem',
            borderRadius: '1.2rem',
            fontWeight: 700,
            textDecoration: 'none',
            border: '2px solid #FF6B35',
            transition: 'background 0.2s, color 0.2s',
          }}
            onMouseOver={e => { e.target.style.background = '#fff'; e.target.style.color = '#FF6B35'; }}
            onMouseOut={e => { e.target.style.background = '#FF6B35'; e.target.style.color = '#fff'; }}
          >Register</Link>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
