import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';
import axios from 'axios';

function Plans() {
  const navigate = useNavigate();
  const { user } = React.useContext(UserContext);
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPlans = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await axios.get('/api/auth/plans');
        setPlans(res.data.plans);
      } catch (err) {
        setError('Could not fetch plans.');
      }
      setLoading(false);
    };
    fetchPlans();
  }, []);

  const handleBuy = (planName) => {
    if (!user) {
      navigate('/login');
    } else {
      navigate(`/payment/${planName.toLowerCase()}`);
    }
  };

  return (
    <div className="plans-page">
      {/* Hero Section */}
      <section className="plans-hero">
        <div className="container">
          <div className="hero-content">
            <h1 className="hero-title">
              Choose Your <span className="gradient-text">Perfect Plan</span>
            </h1>
            <p className="hero-subtitle">
              Select the plan that best fits your needs and budget. All plans include our premium support and quality guarantee.
            </p>
          </div>
        </div>
      </section>

      {/* Plans Section */}
      <section className="plans-section section">
        <div className="container">
          {loading ? (
            <div className="loading-state">
              <div className="loading-spinner"></div>
              <p>Loading plans...</p>
            </div>
          ) : error ? (
            <div className="error-state">
              <div className="error-icon">‚ö†Ô∏è</div>
              <p>{error}</p>
              <button onClick={() => window.location.reload()} className="btn btn-primary">
                Try Again
              </button>
            </div>
          ) : (
            <div className="plans-grid">
              {plans.map((plan, idx) => (
                <div 
                  key={plan._id} 
                  className={`plan-card card ${plan.highlight ? 'featured' : ''}`}
                >
                  {plan.highlight && (
                    <div className="plan-badge">
                      <span>üèÜ Best Value</span>
                    </div>
                  )}
                  
                  <div className="plan-header">
                    <h2 className="plan-name" style={{ color: plan.color }}>
                      {plan.name}
                    </h2>
                    <div className="plan-pricing">
                      <div className="plan-price" style={{ color: plan.color }}>
                        {plan.price}
                      </div>
                      {plan.oldPrice && (
                        <div className="plan-old-price">
                          {plan.oldPrice}
                        </div>
                      )}
                      {plan.save && (
                        <div className="plan-savings">
                          {plan.save}
                        </div>
                      )}
                    </div>
                    <div className="plan-duration">
                      Duration: {plan.duration} days
                    </div>
                  </div>

                  <div className="plan-features">
                    <h3>What's Included:</h3>
                    <ul>
                      {plan.features.map((feature, index) => (
                        <li key={index}>
                          <span className="feature-icon" style={{ color: plan.color }}>
                            ‚úì
                          </span>
                          <span>{feature}</span>
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div className="plan-footer">
                    <button
                      className={`plan-button ${plan.highlight ? 'featured' : ''}`}
                      style={{ 
                        background: plan.color,
                        borderColor: plan.color 
                      }}
                      onClick={() => handleBuy(plan.name)}
                    >
                      <span>Get Started</span>
                      <span className="button-icon">‚Üí</span>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* FAQ Section */}
      <section className="faq-section section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Frequently Asked Questions</h2>
            <p className="section-subtitle">
              Got questions? We've got answers.
            </p>
          </div>
          <div className="faq-grid">
            <div className="faq-item card">
              <h3>What's included in all plans?</h3>
              <p>All plans include responsive design, mobile optimization, basic SEO, and our standard support package.</p>
            </div>
            <div className="faq-item card">
              <h3>Can I upgrade my plan later?</h3>
              <p>Yes! You can upgrade your plan at any time. We'll prorate the difference and apply it to your next billing cycle.</p>
            </div>
            <div className="faq-item card">
              <h3>Do you offer refunds?</h3>
              <p>We offer a 30-day money-back guarantee if you're not completely satisfied with our service.</p>
            </div>
            <div className="faq-item card">
              <h3>How long does development take?</h3>
              <p>Development time varies by plan complexity, but most projects are completed within the specified duration.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Modern Styles */}
      <style jsx>{`
        .plans-page {
          background: #181A20;
          min-height: 100vh;
        }

        /* Hero Section */
        .plans-hero {
          background: linear-gradient(135deg, #23272F 0%, #181A20 100%);
          padding: clamp(3rem, 8vw, 5rem) 0;
          text-align: center;
        }

        .hero-content {
          max-width: 800px;
          margin: 0 auto;
        }

        .hero-title {
          font-size: clamp(2.5rem, 6vw, 4rem);
          font-weight: 800;
          margin-bottom: 1.5rem;
          line-height: 1.1;
          color: #E5E7EB;
        }

        .gradient-text {
          background: linear-gradient(135deg, #2ECC71, #0057D9);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .hero-subtitle {
          font-size: clamp(1rem, 3vw, 1.25rem);
          color: #A0AEC0;
          max-width: 600px;
          margin: 0 auto;
          line-height: 1.6;
        }

        /* Loading and Error States */
        .loading-state,
        .error-state {
          text-align: center;
          padding: 4rem 2rem;
          color: #A0AEC0;
        }

        .loading-spinner {
          width: 40px;
          height: 40px;
          border: 3px solid rgba(46, 204, 113, 0.3);
          border-top: 3px solid #2ECC71;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin: 0 auto 1rem;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .error-icon {
          font-size: 3rem;
          margin-bottom: 1rem;
        }

        /* Plans Grid */
        .plans-section {
          background: #181A20;
        }

        .plans-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
          gap: clamp(1.5rem, 4vw, 2rem);
          max-width: 1200px;
          margin: 0 auto;
        }

        .plan-card {
          position: relative;
          padding: 2rem;
          text-align: center;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          border: 2px solid rgba(255, 255, 255, 0.1);
          overflow: hidden;
        }

        .plan-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
        }

        .plan-card.featured {
          border-color: #2ECC71;
          box-shadow: 0 5px 20px rgba(46, 204, 113, 0.2);
          transform: scale(1.02);
        }

        .plan-card.featured:hover {
          transform: scale(1.02) translateY(-5px);
        }

        .plan-badge {
          position: absolute;
          top: -1rem;
          left: 50%;
          transform: translateX(-50%);
          background: linear-gradient(135deg, #2ECC71, #0057D9);
          color: white;
          padding: 0.5rem 1.5rem;
          border-radius: 2rem;
          font-weight: 700;
          font-size: 0.875rem;
          box-shadow: 0 4px 12px rgba(46, 204, 113, 0.3);
        }

        .plan-header {
          margin-bottom: 2rem;
        }

        .plan-name {
          font-size: clamp(1.5rem, 4vw, 2rem);
          font-weight: 800;
          margin-bottom: 1rem;
        }

        .plan-pricing {
          margin-bottom: 1rem;
        }

        .plan-price {
          font-size: clamp(2rem, 5vw, 3rem);
          font-weight: 800;
          margin-bottom: 0.5rem;
        }

        .plan-old-price {
          color: #A0AEC0;
          text-decoration: line-through;
          font-size: 1.125rem;
          margin-bottom: 0.5rem;
        }

        .plan-savings {
          color: #FF6B35;
          font-weight: 700;
          font-size: 1rem;
        }

        .plan-duration {
          color: #A0AEC0;
          font-weight: 600;
          font-size: 0.875rem;
        }

        .plan-features {
          margin-bottom: 2rem;
          text-align: left;
        }

        .plan-features h3 {
          color: #E5E7EB;
          font-size: 1.125rem;
          margin-bottom: 1rem;
          text-align: center;
        }

        .plan-features ul {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .plan-features li {
          display: flex;
          align-items: center;
          gap: 0.75rem;
          margin-bottom: 0.75rem;
          color: #E5E7EB;
        }

        .feature-icon {
          font-weight: 700;
          font-size: 1.125rem;
        }

        .plan-footer {
          margin-top: auto;
        }

        .plan-button {
          width: 100%;
          padding: 1rem 2rem;
          border: 2px solid;
          border-radius: 2rem;
          font-weight: 700;
          font-size: 1.125rem;
          color: white;
          cursor: pointer;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 0.5rem;
          position: relative;
          overflow: hidden;
        }

        .plan-button::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
          transition: left 0.5s;
        }

        .plan-button:hover::before {
          left: 100%;
        }

        .plan-button:hover {
          background: transparent !important;
          color: inherit;
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
        }

        .plan-button.featured {
          background: linear-gradient(135deg, #2ECC71, #0057D9);
          border-color: transparent;
        }

        .button-icon {
          transition: transform 0.3s ease;
        }

        .plan-button:hover .button-icon {
          transform: translateX(5px);
        }

        /* FAQ Section */
        .faq-section {
          background: #23272F;
        }

        .faq-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
          gap: 1.5rem;
        }

        .faq-item {
          padding: 1.5rem;
        }

        .faq-item h3 {
          color: #2ECC71;
          margin-bottom: 1rem;
          font-size: 1.125rem;
        }

        .faq-item p {
          color: #A0AEC0;
          line-height: 1.6;
          margin: 0;
        }

        /* Responsive Design */
        @media (max-width: 900px) {
          .plans-grid {
            grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
          }

          .plan-card.featured {
            transform: none;
          }

          .plan-card.featured:hover {
            transform: translateY(-5px);
          }
        }

        @media (max-width: 600px) {
          .plans-grid {
            grid-template-columns: 1fr;
          }

          .plan-card {
            margin: 0 1rem;
          }

          .faq-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>
    </div>
  );
}

export default Plans;
