import React from 'react';
import { NavLink } from 'react-router-dom';
import './Sidebar.css';

const navItems = [
  { to: '/dashboard', label: 'Dashboard', icon: 'üè†' },
  { to: '/dashboard/support', label: 'Help & Support', icon: 'üí¨' },
  { to: '/dashboard/subscription', label: 'Subscription', icon: 'üì¶' },
  { to: '/dashboard/purchases', label: 'My Purchases', icon: 'üõí' },
  { to: '/dashboard/notifications', label: 'Notifications', icon: 'üîî' },
];

const Sidebar = ({ onLogout, isPlanExpired }) => (
  <>
    <div className="modern-sidebar" role="navigation" aria-label="User dashboard navigation">
      {/* Sidebar Header */}
      <div className="sidebar-header">
        <div className="user-avatar" aria-label="User avatar" tabIndex={0}>
          <span>üë§</span>
        </div>
        <div className="sidebar-title">
          <h2>User Panel</h2>
          <p>Welcome back!</p>
        </div>
      </div>

      {/* Navigation */}
      <nav className="sidebar-nav" role="menu">
        {navItems.map(item => (
          <NavLink
            key={item.to}
            to={item.to}
            className={({ isActive }) => isActive ? 'nav-item active' : 'nav-item'}
            tabIndex={0}
            aria-label={item.label}
            title={item.label}
            role="menuitem"
          >
            <span className="nav-icon" aria-hidden="true">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
            <span className="nav-indicator"></span>
          </NavLink>
        ))}
        {isPlanExpired && (
          <NavLink
            to="/dashboard/expired"
            className="nav-item expired"
            tabIndex={0}
            aria-label="Plan Expired"
            title="Plan Expired"
            role="menuitem"
          >
            <span className="nav-icon" aria-hidden="true">‚è∞</span>
            <span className="nav-label">Plan Expired</span>
            <span className="nav-indicator"></span>
          </NavLink>
        )}
      </nav>

      {/* Logout Button */}
      <button
        onClick={onLogout}
        className="logout-btn"
        tabIndex={0}
        aria-label="Logout"
        title="Logout"
      >
        <span className="logout-icon" aria-hidden="true">üö™</span>
        <span>Logout</span>
      </button>
    </div>

    {/* Mobile Bottom Nav */}
    <nav className="mobile-bottom-nav" role="navigation" aria-label="Mobile user dashboard navigation">
      {navItems.map(item => (
        <NavLink
          key={item.to}
          to={item.to}
          className={({ isActive }) => isActive ? 'bottom-nav-item active' : 'bottom-nav-item'}
          tabIndex={0}
          aria-label={item.label}
          title={item.label}
          role="menuitem"
        >
          <span className="bottom-nav-icon" aria-hidden="true">{item.icon}</span>
          <span className="bottom-nav-label">{item.label}</span>
        </NavLink>
      ))}
      <button
        className="bottom-nav-item logout"
        onClick={onLogout}
        tabIndex={0}
        aria-label="Logout"
        title="Logout"
      >
        <span className="bottom-nav-icon" aria-hidden="true">üö™</span>
        <span className="bottom-nav-label">Logout</span>
      </button>
    </nav>
  </>
);

export default Sidebar;
