import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminRenewalRequests = () => {
  const [renewalRequests, setRenewalRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [rejectReason, setRejectReason] = useState('');
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [showRejectModal, setShowRejectModal] = useState(false);

  useEffect(() => {
    fetchRenewalRequests();
  }, []);

  const fetchRenewalRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/auth/admin/renewal-requests', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRenewalRequests(res.data.subscriptions);
      setLoading(false);
    } catch (err) {
      setError('Could not fetch renewal requests.');
      setLoading(false);
    }
  };

  const handleApprove = async (subscriptionId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/auth/admin/renewal-requests/${subscriptionId}/approve`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchRenewalRequests(); // Refresh the list
    } catch (err) {
      setError('Could not approve renewal request.');
    }
  };

  const handleReject = async () => {
    if (!rejectReason.trim()) {
      setError('Please provide a rejection reason.');
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/auth/admin/renewal-requests/${selectedRequest}/reject`, 
        { reason: rejectReason }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setShowRejectModal(false);
      setRejectReason('');
      setSelectedRequest(null);
      fetchRenewalRequests(); // Refresh the list
    } catch (err) {
      setError('Could not reject renewal request.');
    }
  };

  const openRejectModal = (subscriptionId) => {
    setSelectedRequest(subscriptionId);
    setShowRejectModal(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) return <div style={{ color: '#E5E7EB', textAlign: 'center', padding: '2rem' }}>Loading...</div>;

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>
        Renewal Requests ({renewalRequests.length})
      </h2>
      
      {error && <div style={{ color: '#FF6B35', marginBottom: '1rem' }}>{error}</div>}
      
      {renewalRequests.length === 0 ? (
        <div style={{ 
          background: '#23272F', 
          borderRadius: '1rem', 
          padding: '2rem', 
          textAlign: 'center',
          color: '#9CA3AF'
        }}>
          No pending renewal requests
        </div>
      ) : (
        <div style={{ display: 'grid', gap: '1rem' }}>
          {renewalRequests.map((request) => (
            <div key={request._id} style={{
              background: '#23272F',
              borderRadius: '1rem',
              padding: '1.5rem',
              border: '1px solid #333'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '1rem' }}>
                <div>
                  <h3 style={{ color: '#2ECC71', marginBottom: '0.5rem' }}>
                    {request.user?.name || 'Unknown User'}
                  </h3>
                  <p style={{ color: '#9CA3AF', marginBottom: '0.5rem' }}>
                    {request.user?.email || 'No email'}
                  </p>
                  <p style={{ color: '#E5E7EB' }}>
                    <strong>Plan:</strong> {request.plan.charAt(0).toUpperCase() + request.plan.slice(1)}
                  </p>
                  <p style={{ color: '#E5E7EB' }}>
                    <strong>Current Status:</strong> {request.status}
                  </p>
                  <p style={{ color: '#E5E7EB' }}>
                    <strong>Expires:</strong> {request.expiresAt ? formatDate(request.expiresAt) : 'No expiry date'}
                  </p>
                  <p style={{ color: '#E5E7EB' }}>
                    <strong>Renewal Requested:</strong> {formatDate(request.renewalRequestDate)}
                  </p>
                </div>
                <div style={{ display: 'flex', gap: '0.5rem' }}>
                  <button
                    onClick={() => handleApprove(request._id)}
                    style={{
                      background: '#2ECC71',
                      color: '#181A20',
                      border: 'none',
                      borderRadius: '0.5rem',
                      padding: '0.5rem 1rem',
                      fontWeight: 600,
                      cursor: 'pointer'
                    }}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => openRejectModal(request._id)}
                    style={{
                      background: '#FF6B35',
                      color: '#fff',
                      border: 'none',
                      borderRadius: '0.5rem',
                      padding: '0.5rem 1rem',
                      fontWeight: 600,
                      cursor: 'pointer'
                    }}
                  >
                    Reject
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Reject Modal */}
      {showRejectModal && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: '#23272F',
            borderRadius: '1rem',
            padding: '2rem',
            maxWidth: '500px',
            width: '90%'
          }}>
            <h3 style={{ color: '#FF6B35', marginBottom: '1rem' }}>Reject Renewal Request</h3>
            <p style={{ color: '#E5E7EB', marginBottom: '1rem' }}>
              Please provide a reason for rejecting this renewal request:
            </p>
            <textarea
              value={rejectReason}
              onChange={(e) => setRejectReason(e.target.value)}
              placeholder="Enter rejection reason..."
              style={{
                width: '100%',
                minHeight: '100px',
                padding: '0.8rem',
                borderRadius: '0.5rem',
                border: '1px solid #333',
                background: '#181A20',
                color: '#E5E7EB',
                marginBottom: '1rem',
                resize: 'vertical'
              }}
            />
            <div style={{ display: 'flex', gap: '0.5rem', justifyContent: 'flex-end' }}>
              <button
                onClick={() => {
                  setShowRejectModal(false);
                  setRejectReason('');
                  setSelectedRequest(null);
                }}
                style={{
                  background: '#6B7280',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '0.5rem',
                  padding: '0.5rem 1rem',
                  fontWeight: 600,
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
              <button
                onClick={handleReject}
                style={{
                  background: '#FF6B35',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '0.5rem',
                  padding: '0.5rem 1rem',
                  fontWeight: 600,
                  cursor: 'pointer'
                }}
              >
                Reject
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminRenewalRequests;
