import React, { useEffect, useState } from 'react';
import axios from 'axios';

const planDisplayNames = {
  starter: 'Starter',
  premium: 'Premium',
  pro: 'Pro',
};

const Subscription = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSubscriptions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-subscriptions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubscriptions(res.data.subscriptions);
      } catch (err) {
        setError('Could not fetch subscriptions.');
      }
      setLoading(false);
    };
    fetchSubscriptions();
  }, []);

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '600px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>My Subscription</h2>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
        {loading ? (
          <div>Loading...</div>
        ) : error ? (
          <div style={{ color: '#FF6B35' }}>{error}</div>
        ) : subscriptions.length === 0 ? (
          <div>No subscriptions found.</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {subscriptions.map(sub => (
              <li key={sub._id} style={{ marginBottom: '1.5rem', background: '#181A20', borderRadius: '0.5rem', padding: '1rem' }}>
                <div><b>Plan:</b> {planDisplayNames[sub.plan]}</div>
                <div><b>Subscription ID:</b> {sub.uniqueId}</div>
                <div><b>Status:</b> <span style={{ color: sub.status === 'active' ? '#2ECC71' : sub.status === 'rejected' ? '#FF6B35' : '#A0AEC0', fontWeight: 700 }}>{sub.status}</span></div>
                {sub.status === 'rejected' && sub.rejectionReason && (
                  <div style={{ color: '#FF6B35', marginTop: '0.5rem' }}><b>Rejection Reason:</b> {sub.rejectionReason}</div>
                )}
                {sub.status === 'active' && sub.expiresAt && (
                  <div style={{ color: '#A0AEC0', marginTop: '0.5rem' }}>
                    <b>Expires At:</b> {new Date(sub.expiresAt).toLocaleDateString()}
                  </div>
                )}
                {sub.status === 'active' && sub.expiresAt && (new Date(sub.expiresAt) - new Date() < 10 * 24 * 60 * 60 * 1000) && (
                  <button onClick={() => window.location.href = '/plans'} style={{ marginTop: '0.7rem', background: '#FF6B35', color: '#fff', border: 'none', borderRadius: '0.5rem', padding: '0.5rem 1.2rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Upgrade Plan</button>
                )}
                <div style={{ fontSize: '0.95rem', color: '#A0AEC0' }}>Created: {new Date(sub.createdAt).toLocaleString()}</div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Subscription;
