            <p>{user?.role || 'User'}</p>
          </div>
        </div>
        <div className="info-card">
          <div className="info-icon">📧</div>
          <div className="info-content">
            <h3>Email</h3>
            <p>{user?.email || 'N/A'}</p>
          </div>
        </div>
        <div className="info-card">
          <div className="info-icon">📅</div>
          <div className="info-content">
            <h3>Member Since</h3>
            <p>{user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}</p>
