import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Services.css';

function Services() {
  const [isVisible, setIsVisible] = useState(false);
  const miniServices = [
    { title: 'Web Design', icon: 'ðŸŽ¨', color: '#4facfe' },
    { title: 'Web Development', icon: 'ðŸ’»', color: '#667eea' },
    { title: 'E-commerce', icon: 'ðŸ›’', color: '#764ba2' },
    { title: 'UI/UX', icon: 'ðŸ§©', color: '#f093fb' },
    { title: 'Maintenance', icon: 'ðŸ”§', color: '#43e97b' },
    { title: 'SEO/Marketing', icon: 'ðŸš€', color: '#fa709a' }
  ];

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <div className="services-page">
      <section className="services-section">
        <div className="container">
          {/* Section Header */}
          <div className="section-header">
            <h2 className="section-title">Our Services</h2>
            <p className="section-subtitle">
              Comprehensive web development solutions tailored to your business needs
            </p>
          </div>

          {/* Mini Services - Quick Overview */}
          <div className="mini-services">
            <div className="mini-services-grid">
              {miniServices.map((item, idx) => (
                <Link
                  to="/contact"
                  key={item.title}
                  className={`mini-card ${isVisible ? 'animate-in' : ''}`}
                  style={{ animationDelay: `${idx * 0.06}s` }}
                  aria-label={`Get ${item.title}`}
                >
                  <span
                    className="mini-icon"
                    style={{ background: `${item.color}20`, color: item.color }}
                  >
                    {item.icon}
                  </span>
                  <span className="mini-title">{item.title}</span>
                </Link>
              ))}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Services;
