import React, { useState } from 'react';

const testimonials = [
  {
    name: 'Amit Sharma',
    role: 'Business Owner',
    quote: 'Custom Web delivered our website on time and exceeded our expectations. Their support is top-notch!',
    color: '#2ECC71',
  },
  {
    name: 'Priya Singh',
    role: 'Educator',
    quote: 'The education website they built for us is modern, fast, and easy to use. Highly recommended!',
    color: '#0057D9',
  },
  {
    name: 'Rahul Verma',
    role: 'Startup Founder',
    quote: 'Professional, creative, and reliable. Custom Web is our go-to for all web solutions.',
    color: '#FF6B35',
  },
];

function Home() {
  const [current, setCurrent] = useState(0);
  const next = () => setCurrent((current + 1) % testimonials.length);
  const prev = () => setCurrent((current - 1 + testimonials.length) % testimonials.length);

  return (
    <div style={{ background: '#181A20', minHeight: '100vh' }}>
      {/* Hero Section */}
      <section className="responsive-section home-hero" style={{
        background: 'linear-gradient(120deg, #0057D9 60%, #2ECC71 100%)',
        color: '#E5E7EB',
        padding: '4rem 2rem 3rem 2rem',
        textAlign: 'center',
        borderBottomLeftRadius: '2rem',
        borderBottomRightRadius: '2rem',
        boxShadow: '0 4px 24px rgba(0,0,0,0.08)'
      }}>
        <h1 style={{ fontSize: '2.8rem', fontWeight: 800, marginBottom: '1rem', letterSpacing: '1px', color: '#E5E7EB' }}>
          Creative & Innovative Digital Solutions
        </h1>
        <p style={{ fontSize: '1.25rem', marginBottom: '2rem', fontWeight: 400, color: '#E5E7EB' }}>
          We design and develop custom websites to help your business grow online.
        </p>
        <a href="/contact" className="home-cta-btn" style={{
          background: '#FF6B35',
          color: '#E5E7EB',
          padding: '0.9rem 2.2rem',
          borderRadius: '2rem',
          fontWeight: 700,
          fontSize: '1.1rem',
          textDecoration: 'none',
          boxShadow: '0 2px 8px rgba(0,0,0,0.10)',
          transition: 'background 0.2s',
          border: '2px solid #FF6B35',
          display: 'inline-block',
        }}
        onMouseOver={e => e.target.style.background = '#0057D9'}
        onMouseOut={e => e.target.style.background = '#FF6B35'}
        >Get Started</a>
      </section>

      {/* Features Section */}
      <section className="responsive-section responsive-flex home-features" style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '2rem',
        padding: '3rem 1rem',
        maxWidth: '1100px',
        margin: '0 auto',
      }}>
        <div className="responsive-card" style={{
          background: '#23272F',
          borderRadius: '1rem',
          boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
          padding: '2rem',
          flex: '1 1 250px',
          minWidth: '250px',
          maxWidth: '320px',
          textAlign: 'center',
        }}>
          <div style={{ fontSize: '2.5rem', color: '#FF6B35', marginBottom: '1rem' }}>💡</div>
          <h2 style={{ fontWeight: 700, marginBottom: '0.5rem', color: '#E5E7EB' }}>Custom Web Design</h2>
          <p style={{ color: '#E5E7EB' }}>Modern, responsive, and tailored to your business needs.</p>
        </div>
        <div className="responsive-card" style={{
          background: '#23272F',
          borderRadius: '1rem',
          boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
          padding: '2rem',
          flex: '1 1 250px',
          minWidth: '250px',
          maxWidth: '320px',
          textAlign: 'center',
        }}>
          <div style={{ fontSize: '2.5rem', color: '#2ECC71', marginBottom: '1rem' }}>🔒</div>
          <h2 style={{ fontWeight: 700, marginBottom: '0.5rem', color: '#E5E7EB' }}>Secure & Reliable</h2>
          <p style={{ color: '#E5E7EB' }}>We use the latest technologies to keep your site safe and fast.</p>
        </div>
        <div className="responsive-card" style={{
          background: '#23272F',
          borderRadius: '1rem',
          boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
          padding: '2rem',
          flex: '1 1 250px',
          minWidth: '250px',
          maxWidth: '320px',
          textAlign: 'center',
        }}>
          <div style={{ fontSize: '2.5rem', color: '#0057D9', marginBottom: '1rem' }}>🤝</div>
          <h2 style={{ fontWeight: 700, marginBottom: '0.5rem', color: '#E5E7EB' }}>24/7 Support</h2>
          <p style={{ color: '#E5E7EB' }}>Our team is always here to help you with any questions or issues.</p>
        </div>
      </section>

      {/* Trust/Stats Section */}
      <section className="responsive-section responsive-flex home-stats" style={{
        background: '#23272F',
        borderRadius: '1rem',
        boxShadow: '0 2px 12px rgba(0,0,0,0.04)',
        margin: '2rem auto',
        maxWidth: '900px',
        padding: '2rem 1rem',
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '2rem',
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ color: '#2ECC71', fontWeight: 800, fontSize: '2rem' }}>2+</div>
          <div style={{ color: '#E5E7EB', fontWeight: 600 }}>Years Experience</div>
        </div>
        <div style={{ textAlign: 'center' }}>
          <div style={{ color: '#2ECC71', fontWeight: 800, fontSize: '2rem' }}>10+</div>
          <div style={{ color: '#E5E7EB', fontWeight: 600 }}>Satisfied Clients</div>
        </div>
        <div style={{ textAlign: 'center' }}>
          <div style={{ color: '#2ECC71', fontWeight: 800, fontSize: '2rem' }}>12+</div>
          <div style={{ color: '#E5E7EB', fontWeight: 600 }}>Projects Completed</div>
        </div>
      </section>

      {/* Testimonial Slider Section */}
      <section className="responsive-section home-testimonials" style={{
        maxWidth: '500px',
        margin: '2.5rem auto 0 auto',
        padding: '2.5rem 1rem',
        background: '#23272F',
        borderRadius: '1.5rem',
        boxShadow: '0 2px 16px rgba(0,0,0,0.10)',
        position: 'relative',
        minHeight: '320px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '2rem', marginBottom: '2rem', textAlign: 'center' }}>
          What Our Clients Say
        </h2>
        <button onClick={prev} aria-label="Previous" className="testimonial-arrow" style={{
          position: 'absolute',
          left: 20,
          top: '50%',
          transform: 'translateY(-50%)',
          background: 'none',
          border: 'none',
          color: '#FF6B35',
          fontSize: '2rem',
          cursor: 'pointer',
          zIndex: 2,
        }}>&#8592;</button>
        <div className="responsive-card" style={{
          background: '#181A20',
          borderRadius: '1rem',
          boxShadow: '0 2px 12px rgba(0,0,0,0.10)',
          padding: '2rem 1.2rem',
          minWidth: '220px',
          maxWidth: '320px',
          color: '#E5E7EB',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          textAlign: 'center',
          border: `2px solid ${testimonials[current].color}`,
          transition: 'border 0.3s, box-shadow 0.3s',
        }}>
          <div style={{ fontSize: '2.2rem', color: testimonials[current].color, marginBottom: '0.7rem' }}>“</div>
          <div style={{ fontStyle: 'italic', marginBottom: '1.2rem', color: '#E5E7EB' }}>{testimonials[current].quote}</div>
          <div style={{ fontWeight: 700, color: testimonials[current].color }}>{testimonials[current].name}</div>
          <div style={{ color: '#A0AEC0', fontSize: '0.98rem' }}>{testimonials[current].role}</div>
        </div>
        <button onClick={next} aria-label="Next" className="testimonial-arrow" style={{
          position: 'absolute',
          right: 20,
          top: '50%',
          transform: 'translateY(-50%)',
          background: 'none',
          border: 'none',
          color: '#FF6B35',
          fontSize: '2rem',
          cursor: 'pointer',
          zIndex: 2,
        }}>&#8594;</button>
      </section>
      {/* Responsive Styles */}
      <style>{`
        @media (max-width: 900px) {
          .home-hero {
            padding: 2.5rem 0.7rem 2rem 0.7rem !important;
            border-radius: 0 0 1.2rem 1.2rem !important;
          }
          .home-features, .home-stats {
            flex-direction: column !important;
            gap: 1.2rem !important;
            padding: 2rem 0.5rem !important;
          }
          .home-testimonials {
            padding: 1.5rem 0.5rem !important;
            min-height: 220px !important;
          }
          .testimonial-arrow {
            font-size: 1.5rem !important;
            left: 8px !important;
            right: 8px !important;
          }
        }
        @media (max-width: 600px) {
          .home-hero {
            padding: 1.2rem 0.2rem 1.2rem 0.2rem !important;
          }
          .responsive-card {
            min-width: 90vw !important;
            max-width: 98vw !important;
            padding: 1.2rem 0.5rem !important;
          }
        }
      `}</style>
    </div>
  );
}

export default Home;
