import React, { useState, useContext } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../UserContext';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { setUser } = useContext(UserContext);

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    try {
      const res = await axios.post('/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      setUser(res.data.user);
      setLoading(false);
      if (res.data.user && res.data.user.role === 'admin') {
        navigate('/admin-dashboard');
      } else {
        navigate('/dashboard');
      }
    } catch (err) {
      setLoading(false);
      setMessage(err.response?.data?.message || 'Login failed.');
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = '/api/auth/google';
  };

  return (
    <div style={{ background: '#181A20', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <form onSubmit={handleSubmit} className="responsive-card" style={{
        background: '#23272F',
        color: '#E5E7EB',
        padding: '2.5rem 2rem',
        borderRadius: '1.2rem',
        boxShadow: '0 2px 16px rgba(0,0,0,0.12)',
        minWidth: '320px',
        maxWidth: '350px',
        width: '100%',
        display: 'flex',
        flexDirection: 'column',
        gap: '1.2rem',
        border: '2px solid #0057D9',
      }}>
        <h2 style={{ color: '#2ECC71', fontWeight: 700, marginBottom: '0.5rem', textAlign: 'center', fontSize: '2rem' }}>Login</h2>
        {location.state?.success && (
          <div style={{ color: '#2ECC71', textAlign: 'center', marginBottom: '0.5rem', fontWeight: 600 }}>
            {location.state.success}
          </div>
        )}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '0.5rem',
            border: '1px solid #333',
            fontSize: '1rem',
            background: '#23272F',
            color: '#E5E7EB',
          }}
        />
        <div style={{ position: 'relative' }}>
          <input
            type={showPassword ? 'text' : 'password'}
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            style={{
              padding: '0.8rem',
              borderRadius: '0.5rem',
              border: '1px solid #333',
              fontSize: '1rem',
              background: '#23272F',
              color: '#E5E7EB',
              width: '100%',
            }}
          />
          <span
            onClick={() => setShowPassword(s => !s)}
            style={{
              position: 'absolute',
              right: '1rem',
              top: '50%',
              transform: 'translateY(-50%)',
              cursor: 'pointer',
              color: '#A0AEC0',
              fontSize: '1.1rem',
              userSelect: 'none',
            }}
            title={showPassword ? 'Hide Password' : 'Show Password'}
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>
        <button type="submit" style={{
          background: '#0057D9',
          color: '#E5E7EB',
          padding: '0.9rem',
          border: 'none',
          borderRadius: '0.5rem',
          fontWeight: 700,
          fontSize: '1.1rem',
          cursor: 'pointer',
          marginTop: '0.5rem',
          borderBottom: '3px solid #2ECC71',
        }}>{loading ? 'Logging in...' : 'Login'}</button>
        <div style={{ textAlign: 'center', fontSize: '0.95rem' }}>
          Don't have an account?{' '}
          <Link to="/register" style={{ color: '#FF6B35', textDecoration: 'underline' }}>Register</Link>
        </div>
        {message && <div style={{ color: '#FF6B35', textAlign: 'center', marginTop: '0.5rem' }}>{message}</div>}
        
        <div style={{ textAlign: 'center', marginTop: '1rem', color: '#A0AEC0' }}>OR</div>

        <button type="button" onClick={handleGoogleLogin} style={{
          background: '#DB4437',
          color: 'white',
          padding: '0.9rem',
          border: 'none',
          borderRadius: '0.5rem',
          fontWeight: 700,
          fontSize: '1.1rem',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '0.5rem'
        }}>
          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="white"><path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" /><path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" /><path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l3.66-2.84z" /><path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" /><path d="M1 1h22v22H1z" fill="none" /></svg>
          Sign in with Google
        </button>
      </form>
    </div>
  );
}

export default Login;
