import React, { useContext, useState } from 'react';
import { UserContext } from '../UserContext';

const AccountSettings = () => {
  const { user, setUser } = useContext(UserContext);
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({ name: user?.name || '', email: user?.email || '' });
  const [passwordMode, setPasswordMode] = useState(false);
  const [passwordForm, setPasswordForm] = useState({ current: '', new: '', confirm: '' });
  const [msg, setMsg] = useState('');
  const [err, setErr] = useState('');
  const [pwMsg, setPwMsg] = useState('');
  const [pwErr, setPwErr] = useState('');

  const handleEditChange = e => setForm(f => ({ ...f, [e.target.name]: e.target.value }));
  const handlePasswordChange = e => setPasswordForm(f => ({ ...f, [e.target.name]: e.target.value }));

  const handleProfileSave = async () => {
    setMsg(''); setErr('');
    try {
      const res = await fetch('/api/user/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
        credentials: 'include',
      });
      const data = await res.json();
      if (res.ok) {
        setMsg('Profile updated!');
        setUser(u => ({ ...u, ...form }));
        setEditMode(false);
      } else {
        setErr(data.message || 'Update failed');
      }
    } catch (e) {
      setErr('Network error');
    }
  };

  const handlePasswordSave = async () => {
    setPwMsg(''); setPwErr('');
    if (passwordForm.new !== passwordForm.confirm) {
      setPwErr('Passwords do not match');
      return;
    }
    try {
      const res = await fetch('/api/user/change-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(passwordForm),
        credentials: 'include',
      });
      const data = await res.json();
      if (res.ok) {
        setPwMsg('Password changed!');
        setPasswordMode(false);
        setPasswordForm({ current: '', new: '', confirm: '' });
      } else {
        setPwErr(data.message || 'Change failed');
      }
    } catch (e) {
      setPwErr('Network error');
    }
  };

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '500px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>Account Settings</h2>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)', marginBottom: '2rem' }}>
        <div style={{ marginBottom: '1.2rem' }}>
          <div><b>Name:</b> {user?.name}</div>
          <div><b>Email:</b> {user?.email}</div>
          <div><b>Role:</b> {user?.role}</div>
          <div><b>Joined:</b> {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : ''}</div>
        </div>
        {msg && <div style={{ color: '#2ECC71', marginBottom: '0.7rem' }}>{msg}</div>}
        {err && <div style={{ color: '#FF6B35', marginBottom: '0.7rem' }}>{err}</div>}
        {!editMode ? (
          <button onClick={() => setEditMode(true)} style={{ background: '#2ECC71', color: '#181A20', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Edit Profile</button>
        ) : (
          <form style={{ marginTop: '1rem' }} onSubmit={e => { e.preventDefault(); handleProfileSave(); }}>
            <input name="name" value={form.name} onChange={handleEditChange} placeholder="Name" style={{ width: '100%', marginBottom: '0.7rem', padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }} />
            <input name="email" value={form.email} onChange={handleEditChange} placeholder="Email" style={{ width: '100%', marginBottom: '0.7rem', padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }} />
            <button type="submit" style={{ background: '#2ECC71', color: '#181A20', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer', marginRight: '0.7rem' }}>Save</button>
            <button type="button" onClick={() => setEditMode(false)} style={{ background: '#23272F', color: '#E5E7EB', border: '1px solid #A0AEC0', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Cancel</button>
          </form>
        )}
      </div>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
        <div style={{ marginBottom: '1.2rem' }}><b>Change Password</b></div>
        {pwMsg && <div style={{ color: '#2ECC71', marginBottom: '0.7rem' }}>{pwMsg}</div>}
        {pwErr && <div style={{ color: '#FF6B35', marginBottom: '0.7rem' }}>{pwErr}</div>}
        {!passwordMode ? (
          <button onClick={() => setPasswordMode(true)} style={{ background: '#FF6B35', color: '#fff', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Change Password</button>
        ) : (
          <form style={{ marginTop: '1rem' }} onSubmit={e => { e.preventDefault(); handlePasswordSave(); }}>
            <input name="current" type="password" value={passwordForm.current} onChange={handlePasswordChange} placeholder="Current Password" style={{ width: '100%', marginBottom: '0.7rem', padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }} />
            <input name="new" type="password" value={passwordForm.new} onChange={handlePasswordChange} placeholder="New Password" style={{ width: '100%', marginBottom: '0.7rem', padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }} />
            <input name="confirm" type="password" value={passwordForm.confirm} onChange={handlePasswordChange} placeholder="Confirm New Password" style={{ width: '100%', marginBottom: '0.7rem', padding: '0.7rem', borderRadius: '0.5rem', border: '1px solid #333', background: '#181A20', color: '#E5E7EB' }} />
            <button type="submit" style={{ background: '#FF6B35', color: '#fff', border: 'none', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer', marginRight: '0.7rem' }}>Save</button>
            <button type="button" onClick={() => setPasswordMode(false)} style={{ background: '#23272F', color: '#E5E7EB', border: '1px solid #A0AEC0', borderRadius: '0.5rem', padding: '0.7rem 1.5rem', fontWeight: 700, fontSize: '1rem', cursor: 'pointer' }}>Cancel</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default AccountSettings;
