import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    // Placeholder for login logic
    setMessage('Login functionality coming soon!');
  };

  return (
    <div style={{ background: '#F5F5F5', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <form onSubmit={handleSubmit} style={{
        background: '#fff',
        padding: '2.5rem 2rem',
        borderRadius: '1.2rem',
        boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
        minWidth: '320px',
        maxWidth: '350px',
        width: '100%',
        display: 'flex',
        flexDirection: 'column',
        gap: '1.2rem',
      }}>
        <h2 style={{ color: '#2E2E2E', fontWeight: 700, marginBottom: '0.5rem', textAlign: 'center' }}>Login</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '0.5rem',
            border: '1px solid #ddd',
            fontSize: '1rem',
          }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
          style={{
            padding: '0.8rem',
            borderRadius: '0.5rem',
            border: '1px solid #ddd',
            fontSize: '1rem',
          }}
        />
        <button type="submit" style={{
          background: '#0057D9',
          color: '#fff',
          padding: '0.9rem',
          border: 'none',
          borderRadius: '0.5rem',
          fontWeight: 700,
          fontSize: '1.1rem',
          cursor: 'pointer',
          marginTop: '0.5rem',
        }}>Login</button>
        <div style={{ textAlign: 'center', fontSize: '0.95rem' }}>
          Don't have an account?{' '}
          <Link to="/register" style={{ color: '#0057D9', textDecoration: 'underline' }}>Register</Link>
        </div>
        {message && <div style={{ color: '#2ECC71', textAlign: 'center', marginTop: '0.5rem' }}>{message}</div>}
      </form>
    </div>
  );
}

export default Login;
