import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  const { user, setUser } = useContext(UserContext);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setDropdownOpen(false);
    navigate('/login');
  };

  // Helper for avatar
  const getAvatar = () => {
    if (user && user.name) {
      return user.name[0].toUpperCase();
    }
    return <span role="img" aria-label="User">üë§</span>;
  };

  return (
    <>
      <nav className={`modern-navbar pro-navbar ${isScrolled ? 'scrolled' : ''}`} aria-label="Main Navigation">
        <div className="container">
          <div className="navbar-content">
            {/* Logo */}
            <Link to="/" className="navbar-logo" aria-label="Home">
              <span className="logo-text">CUSTOM WEB</span>
            </Link>

            {/* Desktop Navigation */}
            <div className="navbar-nav desktop-nav">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`nav-link ${location.pathname === link.to ? 'active' : ''}`}
                  tabIndex={0}
                >
                  {link.label}
                </Link>
              ))}
            </div>

            {/* Auth Buttons / User Dropdown */}
            <div className="navbar-auth desktop-auth same-line-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                    aria-label="User menu"
                  >
                    <span className="user-avatar">{getAvatar()}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="user-caret">‚ñº</span>
                  </button>
                  <div className={`user-dropdown-menu pro-dropdown ${dropdownOpen ? 'open' : ''}`} tabIndex={-1} onMouseLeave={() => setDropdownOpen(false)}>
                    <Link to={user.role === 'admin' ? '/admin-dashboard' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                      {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                    </Link>
                    <div className="dropdown-divider"></div>
                    <button className="dropdown-item logout-btn" onClick={handleLogout}>
                      Logout
                    </button>
                  </div>
                </div>
              ) : (
                <div className="auth-buttons">
                  <Link to='/login' className="btn btn-primary" aria-label="Login">
                    <span role="img" aria-label="Login">üîë</span> Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary" aria-label="Register">
                    <span role="img" aria-label="Register">üìù</span> Register
                  </Link>
                </div>
              )}
            </div>

            {/* Mobile Menu Button */}
            <button 
              className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={toggleMobileMenu}
              aria-label="Toggle mobile menu"
              aria-expanded={isMobileMenuOpen}
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </div>
        </div>

        {/* Mobile Menu Overlay */}
        <div className={`mobile-menu-overlay ${isMobileMenuOpen ? 'active' : ''}`} tabIndex={-1} aria-hidden={!isMobileMenuOpen}>
          <div className="mobile-menu-content">
            <div className="mobile-nav-links">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`mobile-nav-link ${location.pathname === link.to ? 'active' : ''}`}
                  tabIndex={isMobileMenuOpen ? 0 : -1}
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  {link.label}
                </Link>
              ))}
            </div>
            <div className="mobile-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                    aria-label="User menu"
                  >
                    <span className="user-avatar">{getAvatar()}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="user-caret">‚ñº</span>
                  </button>
                  <div className={`user-dropdown-menu pro-dropdown ${dropdownOpen ? 'open' : ''}`} tabIndex={-1} onMouseLeave={() => setDropdownOpen(false)}>
                    <Link to={user.role === 'admin' ? '/admin-dashboard' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                      {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                    </Link>
                    <div className="dropdown-divider"></div>
                    <button className="dropdown-item logout-btn" onClick={handleLogout}>
                      Logout
                    </button>
                  </div>
                </div>
              ) : (
                <div className="mobile-auth-buttons">
                  <Link to='/login' className="btn btn-primary" aria-label="Login">
                    <span role="img" aria-label="Login">üîë</span> Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary" aria-label="Register">
                    <span role="img" aria-label="Register">üìù</span> Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Navbar Styles */}
      <style jsx>{`
        :root {
          --primary: #5A31F4;
          --secondary: #00C4CC;
          --accent: #FFB400;
          --dark: #222;
          --bg: #F7F8FA;
        }
        .pro-navbar {
          box-shadow: 0 2px 16px rgba(90,49,244,0.08);
          font-family: 'Inter', 'Segoe UI', Arial, sans-serif;
        }
        .modern-navbar {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: rgba(35, 39, 47, 0.97);
          backdrop-filter: blur(10px);
          border-bottom: 1.5px solid var(--primary);
          z-index: 1000;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          min-height: 56px;
        }
        .modern-navbar.scrolled {
          background: rgba(35, 39, 47, 0.99);
          box-shadow: 0 6px 24px rgba(90,49,244,0.13);
        }
        .navbar-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 0.7rem 0;
          min-height: 56px;
        }
        .navbar-logo {
          display: flex;
          align-items: center;
          text-decoration: none;
        }
        .same-line-auth {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .logo-text {
          font-weight: 900;
          font-size: clamp(1.3rem, 2.8vw, 1.7rem);
          background: linear-gradient(90deg, var(--primary), var(--accent));
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          letter-spacing: 1.5px;
        }
        .navbar-nav {
          display: flex;
          align-items: center;
          gap: clamp(1.1rem, 3vw, 2rem);
        }
        .nav-link {
          color: #fff;
          text-decoration: none;
          font-weight: 600;
          font-size: clamp(1rem, 2vw, 1.1rem);
          padding: 0.3rem 0;
          position: relative;
          letter-spacing: 0.2px;
          transition: color 0.2s, font-weight 0.2s;
        }
        .nav-link::after {
          content: '';
          position: absolute;
          bottom: -4px;
          left: 0;
          width: 0;
          height: 2.5px;
          background: linear-gradient(90deg, var(--primary), var(--accent));
          border-radius: 2px;
          transition: width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .nav-link:hover,
        .nav-link.active {
          color: var(--secondary);
          font-weight: 800;
        }
        .nav-link.active::after,
        .nav-link:hover::after {
          width: 100%;
        }
        .btn.btn-primary {
          background: linear-gradient(90deg, var(--primary), var(--secondary));
          color: #fff;
          border-radius: 24px;
          border: 2px solid var(--primary);
          font-weight: 700;
          padding: 8px 24px;
          margin-left: 8px;
          transition: background 0.2s, color 0.2s, border 0.2s, transform 0.2s;
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .btn.btn-primary:hover {
          background: linear-gradient(90deg, var(--secondary), var(--accent));
          color: #fff;
          border-color: var(--accent);
          transform: scale(1.05);
        }
        .btn.btn-secondary {
          background: none;
          color: var(--primary);
          border: 2px solid #fff;
          border-radius: 24px;
          font-weight: 700;
          padding: 8px 24px;
          margin-left: 8px;
          transition: background 0.2s, color 0.2s, border 0.2s, transform 0.2s;
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .btn.btn-secondary:hover {
          background: var(--primary);
          color: #fff;
          border-color: var(--accent);
          transform: scale(1.05);
        }
        .user-dropdown-wrapper {
          position: relative;
        }
        .user-btn.user-dropdown-toggle {
          background: none;
          border: none;
          color: #fff;
          font-weight: 700;
          font-size: 1.1rem;
          display: flex;
          align-items: center;
          gap: 0.6rem;
          cursor: pointer;
          padding: 0.3rem 0.7rem;
          border-radius: 18px;
          transition: background 0.2s;
        }
        .user-btn.user-dropdown-toggle:hover, .user-btn.user-dropdown-toggle:focus {
          background: rgba(90,49,244,0.08);
        }
        .user-avatar {
          width: 2.1rem;
          height: 2.1rem;
          background: linear-gradient(135deg, var(--primary), var(--secondary));
          color: #fff;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.2rem;
          font-weight: 800;
          box-shadow: 0 2px 8px rgba(90,49,244,0.10);
        }
        .user-caret {
          font-size: 0.8rem;
          margin-left: 0.2rem;
        }
        .user-dropdown-menu.pro-dropdown {
          position: absolute;
          top: 120%;
          right: 0;
          min-width: 180px;
          background: #fff;
          color: var(--dark);
          border: 1.5px solid var(--primary);
          border-radius: 14px;
          box-shadow: 0 8px 32px rgba(90,49,244,0.13);
          opacity: 0;
          pointer-events: none;
          transform: translateY(-10px) scale(0.98);
          transition: opacity 0.25s, transform 0.25s;
          z-index: 100;
        }
        .user-dropdown-menu.pro-dropdown.open {
          opacity: 1;
          pointer-events: auto;
          transform: translateY(0) scale(1);
        }
        .dropdown-item {
          padding: 0.9rem 1.2rem;
          font-size: 1rem;
          color: var(--dark);
          background: none;
          border: none;
          width: 100%;
          text-align: left;
          cursor: pointer;
          transition: background 0.18s, color 0.18s;
        }
        .dropdown-item:hover {
          background: var(--bg);
          color: var(--primary);
        }
        .dropdown-divider {
          height: 1px;
          background: linear-gradient(90deg, var(--primary), var(--accent));
          margin: 0.2rem 0;
          border: none;
        }
        .logout-btn {
          color: #ff4444;
        }
        .mobile-menu-btn {
          display: none;
          flex-direction: column;
          gap: 4px;
          background: none;
          border: none;
          cursor: pointer;
          z-index: 1100;
        }
        .mobile-menu-btn span {
          display: block;
          width: 28px;
          height: 3px;
          background: var(--primary);
          border-radius: 2px;
          transition: all 0.3s;
        }
        @media (max-width: 900px) {
          .desktop-nav, .desktop-auth {
            display: none;
          }
          .mobile-menu-btn {
            display: flex;
          }
        }
        .mobile-menu-overlay {
          display: none;
        }
        .mobile-menu-overlay.active {
          display: block;
          position: fixed;
          top: 0; left: 0; right: 0; bottom: 0;
          background: rgba(35, 39, 47, 0.98);
          z-index: 2000;
          animation: fadeIn 0.3s;
        }
        .mobile-menu-content {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: flex-start;
          padding: 4.5rem 1.5rem 2rem 1.5rem;
          min-height: 100vh;
        }
        .mobile-nav-links {
          display: flex;
          flex-direction: column;
          gap: 2rem;
          width: 100%;
          align-items: center;
        }
        .mobile-nav-link {
          color: #fff;
          font-weight: 700;
          font-size: 1.3rem;
          text-decoration: none;
          transition: color 0.2s;
          position: relative;
        }
        .mobile-nav-link.active,
        .mobile-nav-link:hover {
          color: var(--accent);
        }
        .mobile-auth {
          margin-top: 2.5rem;
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .mobile-auth-buttons .btn {
          width: 100%;
          margin-bottom: 1rem;
          justify-content: center;
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        .auth-buttons {
          display: flex;
          flex-direction: row;
          align-items: center;
          gap: 1rem;
        }
        .btn {
          display: flex;
          align-items: center;
          justify-content: center;
          height: 44px;
          min-width: 120px;
          font-size: 1.08rem;
          font-weight: 700;
          padding: 0 28px;
          box-sizing: border-box;
          transition: background 0.2s, color 0.2s, border 0.2s, transform 0.2s;
          cursor: pointer;
          outline: none;
          user-select: none;
        }
        .btn span[role="img"] {
          margin-right: 0.5em;
          font-size: 1.2em;
          display: flex;
          align-items: center;
        }
        @media (max-width: 900px) {
          .auth-buttons {
            flex-direction: column;
            gap: 0.7rem;
            width: 100%;
          }
          .btn {
            width: 100%;
            min-width: 0;
            padding: 0 0.5rem;
            font-size: 1.1rem;
            height: 48px;
          }
        }
      `}</style>
    </>
  );
}

export default Navbar;
