import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const Hero = () => {
  const textRef = useRef(null);
  const cardRef = useRef(null);
  const mapRef = useRef(null);

  useEffect(() => {
    const targets = [textRef.current, cardRef.current, mapRef.current];
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-in');
          }
        });
      },
      { threshold: 0.2 }
    );
    targets.forEach((el) => el && observer.observe(el));
    return () => observer.disconnect();
  }, []);
  return (
    <section
      id="hero"
      className="hero"
      aria-labelledby="hero-title"
      aria-describedby="hero-subtitle"
    >
      <div className="hero-background" aria-hidden="true">
        <div className="hero-gradient" aria-hidden="true"></div>
        <div className="bihar-abstract" aria-hidden="true"></div>
        <div className="floating-shapes" aria-hidden="true">
          <div className="shape shape-1"></div>
          <div className="shape shape-2"></div>
          <div className="shape shape-3"></div>
          <div className="shape shape-4"></div>
        </div>
      </div>

      <div className="container">
        <div className="hero-content">
          <div className="hero-text" ref={textRef}>
            <div className="badge" aria-label="Brand">Bihar IT Solution</div>
            <h1 className="hero-title" id="hero-title">
              Transforming Bihar with <span className="gradient-text">IT Solutions</span>
            </h1>
            <p className="hero-subtitle" id="hero-subtitle">
              Web | Mobile | Cloud | Consultancy
            </p>
            <div className="hero-actions">
              <Link to="/services" className="btn btn-secondary" aria-label="Explore our services">
                Explore Services
              </Link>
              <Link to="/contact" className="btn btn-primary" aria-label="Contact us">
                Contact Us <span className="btn-icon" aria-hidden="true">→</span>
              </Link>
            </div>
          </div>

          <div className="hero-visual">
            <div className="hero-card" ref={cardRef} role="img" aria-label="Sample code card">
              <div className="card-header">
                <div className="status-dots"><span></span><span></span><span></span></div>
              </div>
              <div className="card-content">
                <div className="code-line"><span className="code-keyword">const</span> website = <span className="code-string">'Amazing'</span>;</div>
                <div className="code-line"><span className="code-keyword">if</span> (website === <span className="code-string">'Amazing'</span>) &#123;</div>
                <div className="code-line indent"><span className="code-function">return</span> <span className="code-string">'Success!'</span>;</div>
                <div className="code-line">&#125;</div>
              </div>
            </div>
          </div>

          <div className="hero-map" ref={mapRef} aria-hidden="true">
            <svg width="300" height="300" viewBox="0 0 300 300" xmlns="http://www.w3.org/2000/svg" focusable="false">
              <defs>
                <linearGradient id="biharGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                  <stop offset="0%" style={{stopColor:'#667eea', stopOpacity:1}} />
                  <stop offset="50%" style={{stopColor:'#764ba2', stopOpacity:1}} />
                  <stop offset="100%" style={{stopColor:'#ff8800', stopOpacity:1}} />
                </linearGradient>
              </defs>
              <path d="M50 50 L250 50 L250 200 L200 220 L180 240 L150 250 L120 240 L100 220 L80 200 L50 180 Z"
                    fill="url(#biharGradient)" stroke="#fff" strokeWidth="2" opacity="0.8"/>
              <title>Bihar region illustration</title>
            </svg>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
