import React, { useEffect, useState } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import axios from 'axios';
import Sidebar from '../components/Sidebar';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    axios.get('/api/dashboard', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setUser(res.data.user);
        setLoading(false);
      })
      .catch(() => {
        localStorage.removeItem('token');
        navigate('/login');
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) return <div style={{ color: '#E5E7EB', textAlign: 'center', marginTop: '4rem' }}>Loading dashboard...</div>;

  return (
    <div style={{ background: '#181A20', minHeight: '100vh', display: 'flex', flexDirection: 'row', position: 'relative' }}>
      {/* Mobile menu button */}
      <button
        onClick={() => setSidebarOpen(o => !o)}
        style={{
          position: 'fixed',
          top: 18,
          left: 18,
          zIndex: 1001,
          background: '#2ECC71',
          color: '#181A20',
          border: 'none',
          borderRadius: '0.7rem',
          padding: '0.7rem 1.2rem',
          fontWeight: 700,
          fontSize: '1.2rem',
          display: 'none',
        }}
        className="dashboard-mobile-menu-btn"
        aria-label="Open sidebar"
      >â˜°</button>
      {/* Sidebar */}
      <div
        style={{
          transition: 'left 0.3s',
          position: 'fixed',
          left: sidebarOpen ? 0 : -270,
          top: 0,
          height: '100vh',
          zIndex: 1000,
          background: '#23272F',
          boxShadow: sidebarOpen ? '0 4px 24px rgba(0,0,0,0.13)' : 'none',
          width: 250,
          minWidth: 250,
          maxWidth: 250,
          display: 'block',
        }}
        className="dashboard-sidebar-mobile"
      >
        <Sidebar onLogout={handleLogout} />
      </div>
      {/* Desktop Sidebar */}
      <div
        style={{
          display: 'block',
          minWidth: 250,
          maxWidth: 250,
        }}
        className="dashboard-sidebar-desktop"
      >
        <Sidebar onLogout={handleLogout} />
      </div>
      {/* Main content */}
      <main style={{ marginLeft: 250, flex: 1, padding: '2.5rem 2rem', minHeight: '100vh', background: '#181A20', width: '100%' }}>
        <Outlet context={{ user }} />
      </main>
      <style>{`
        @media (max-width: 900px) {
          .dashboard-sidebar-desktop { display: none !important; }
          .dashboard-mobile-menu-btn { display: block !important; }
          .dashboard-sidebar-mobile {
            display: block !important;
            left: ${sidebarOpen ? '0' : '-270px'} !important;
            top: 0;
            height: 100vh;
            z-index: 1000;
          }
          main {
            margin-left: 0 !important;
            padding: 1.2rem 0.5rem !important;
          }
        }
        @media (max-width: 600px) {
          main {
            padding: 0.5rem 0.2rem !important;
          }
        }
      `}</style>
    </div>
  );
}

export default Dashboard;
