import React, { useEffect, useState } from 'react';
import axios from 'axios';

const planDisplayNames = {
  starter: 'Starter',
  premium: 'Premium',
  pro: 'Pro',
};

const Subscription = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSubscriptions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/auth/user-subscriptions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSubscriptions(res.data.subscriptions);
      } catch (err) {
        setError('Could not fetch subscriptions.');
      }
      setLoading(false);
    };
    fetchSubscriptions();
  }, []);

  return (
    <div style={{ color: '#E5E7EB', maxWidth: '600px', margin: '0 auto' }}>
      <h2 style={{ color: '#2ECC71', fontWeight: 700, fontSize: '1.7rem', marginBottom: '1.2rem' }}>My Subscription</h2>
      <div style={{ background: '#23272F', borderRadius: '1rem', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.08)' }}>
        {loading ? (
          <div>Loading...</div>
        ) : error ? (
          <div style={{ color: '#FF6B35' }}>{error}</div>
        ) : subscriptions.length === 0 ? (
          <div>No subscriptions found.</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {subscriptions.map(sub => (
              <li key={sub._id} style={{ marginBottom: '1.5rem', background: '#181A20', borderRadius: '0.5rem', padding: '1rem' }}>
                <div><b>Plan:</b> {planDisplayNames[sub.plan]}</div>
                <div><b>Subscription ID:</b> {sub.uniqueId}</div>
                <div><b>Status:</b> <span style={{ color: sub.status === 'active' ? '#2ECC71' : '#FF6B35', fontWeight: 700 }}>{sub.status}</span></div>
                <div style={{ fontSize: '0.95rem', color: '#A0AEC0' }}>Created: {new Date(sub.createdAt).toLocaleString()}</div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Subscription;
