import React from 'react';
import { Link } from 'react-router-dom';
import { services } from '../../data/homeData';

const Services = () => {
  return (
    <section className="services-section">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">Our Services</h2>
          <p className="section-subtitle">
            Comprehensive digital solutions tailored to your business needs
          </p>
        </div>

        <div className="services-grid">
          {services.map((service) => (
            <div key={service.id} className="service-card">
              <div className="service-header">
                <div className="service-icon" style={{ background: service.gradient }}>
                  {service.icon}
                </div>
                <h3 className="service-title">{service.title}</h3>
              </div>
              <p className="service-description">{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, idx) => (
                  <li key={idx} className="feature-item"><span className="feature-check">✓</span>{feature}</li>
                ))}
              </ul>
              <Link to="/services" className="service-link">Learn More <span className="link-arrow">→</span></Link>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
