import React, { useEffect, useState } from 'react';

const steps = [
	{ step: '01', title: 'Discovery & Planning', desc: 'We analyze your requirements and create a detailed project plan.', icon: '🔍' },
	{ step: '02', title: 'Design & Prototyping', desc: 'Create wireframes and design mockups for your approval.', icon: '✏️' },
	{ step: '03', title: 'Development', desc: 'Build your website using best practices and industry standards.', icon: '💻' },
	{ step: '04', title: 'Testing & Launch', desc: 'Thorough testing and deployment to ensure everything works perfectly.', icon: '🚀' },
];

function Process() {
	const [isVisible, setIsVisible] = useState(false);

	useEffect(() => {
		const timer = setTimeout(() => setIsVisible(true), 50);
		return () => clearTimeout(timer);
	}, []);

	return (
		<section className="process-section py-20 bg-gray-50">
			<div className="container max-w-6xl mx-auto px-4">
				<div className="text-center mb-16">
					<h2 className="text-4xl font-bold text-gray-900 mb-4">Our Process</h2>
					<p className="text-xl text-gray-600 max-w-3xl mx-auto">
						A structured approach to delivering exceptional results
					</p>
				</div>
				
				<div className={`process-timeline grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>
					{steps.map((item, idx) => (
						<div 
							key={item.step} 
							className="process-step group relative"
							style={{ transitionDelay: `${idx * 150}ms` }}
						>
							{/* Connecting line for desktop */}
							{idx < steps.length - 1 && (
								<div className="hidden lg:block absolute top-12 left-full w-full h-0.5 bg-gradient-to-r from-blue-400 to-blue-200 z-0 transform translate-x-4"></div>
							)}
							
							<div className="step-card bg-white rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 group-hover:transform group-hover:-translate-y-2 h-full border border-gray-100 overflow-hidden">
								{/* Header with gradient */}
								<div className="step-header bg-gradient-to-r from-blue-600 to-blue-400 px-6 py-4">
									<div className="flex items-center justify-between">
										<div className="step-number bg-white text-blue-600 w-12 h-12 rounded-full flex items-center justify-center font-bold text-lg shadow-md">
											{item.step}
										</div>
										<div className="step-icon text-3xl bg-white bg-opacity-20 rounded-full w-12 h-12 flex items-center justify-center">
											{item.icon}
										</div>
									</div>
								</div>
								
								{/* Content */}
								<div className="step-content p-6">
									<h3 className="text-xl font-semibold text-gray-900 mb-3 group-hover:text-blue-600 transition-colors duration-300">
										{item.title}
									</h3>
									<p className="text-gray-600 leading-relaxed">
										{item.desc}
									</p>
								</div>
								
								{/* Progress indicator */}
								<div className="px-6 pb-4">
									<div className="w-full bg-gray-200 rounded-full h-1.5">
										<div className="bg-gradient-to-r from-blue-400 to-blue-600 h-1.5 rounded-full transition-all duration-500 group-hover:w-full w-3/4"></div>
									</div>
								</div>
							</div>
						</div>
					))}
				</div>
				
				{/* CTA Section */}
				<div className="text-center mt-16">
					<button className="bg-gradient-to-r from-blue-600 to-blue-500 text-white px-8 py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-blue-600 transition-all duration-300 transform hover:-translate-y-1 shadow-lg hover:shadow-xl">
						Start Your Project
					</button>
				</div>
			</div>
		</section>
	);
}

export default Process;
