import React, { useEffect, useState } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import axios from 'axios';
import Sidebar from '../components/Sidebar';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    axios.get('/api/dashboard', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setUser(res.data.user);
        setLoading(false);
      })
      .catch(() => {
        localStorage.removeItem('token');
        navigate('/login');
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) return <div style={{ color: '#E5E7EB', textAlign: 'center', marginTop: '4rem' }}>Loading dashboard...</div>;

  return (
    <div style={{ background: '#181A20', minHeight: '100vh', display: 'flex' }}>
      <Sidebar onLogout={handleLogout} />
      <main style={{ marginLeft: '240px', flex: 1, padding: '2.5rem 2rem', minHeight: '100vh', background: '#181A20' }}>
        <Outlet context={{ user }} />
      </main>
    </div>
  );
}

export default Dashboard;
