import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { UserContext } from '../UserContext';

const navLinks = [
  { to: '/', label: 'Home' },
  { to: '/about', label: 'About' },
  { to: '/plans', label: 'Plans' },
  { to: '/services', label: 'Services' },
  { to: '/features', label: 'Features' },
  { to: '/team', label: 'Our Team' },
  { to: '/contact', label: 'Contact' },
];

function Navbar() {
  const location = useLocation();
  const { user, setUser } = useContext(UserContext);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setDropdownOpen(false);
    navigate('/login');
  };

  // Add this helper to determine dashboard nav links
  const dashboardNavLinks = user && user.role === 'admin' ? [
    { to: '/admin-dashboard/user-plans', label: 'User Plans', icon: 'ðŸ‘¥' },
    { to: '/admin-dashboard/notifications', label: 'Notifications', icon: 'ðŸ””' },
    { to: '/admin-dashboard/plans', label: 'Plans', icon: 'ðŸ“¦' },
  ] : [
    { to: '/dashboard/subscription', label: 'Subscription', icon: 'ðŸ“¦' },
    { to: '/dashboard/purchases', label: 'My Purchases', icon: 'ðŸ›’' },
    { to: '/dashboard/notifications', label: 'Notifications', icon: 'ï¿½ï¿½' },
  ];

  return (
    <>
      <nav className={`modern-navbar ${isScrolled ? 'scrolled' : ''}`}>
        <div className="container">
          <div className="navbar-content">
            {/* Logo */}
            <Link to="/" className="navbar-logo">
              <span className="logo-text">CUSTOM WEB</span>
            </Link>

            {/* Desktop Navigation */}
            <div className="navbar-nav desktop-nav">
              {!user && navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`nav-link ${location.pathname === link.to ? 'active' : ''}`}
                >
                  {link.label}
                </Link>
              ))}
            </div>

            {/* Auth Buttons / User Dropdown */}
            <div className="navbar-auth desktop-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                  >
                    <span className="user-avatar">{user.name ? user.name[0].toUpperCase() : 'U'}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="dropdown-caret">â–¼</span>
                  </button>
                  {dropdownOpen && (
                    <div className="user-dropdown-menu" onMouseLeave={() => setDropdownOpen(false)}>
                      <Link to={user.role === 'admin' ? '/admin-dashboard' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                      </Link>
                      {/* <Link to="/profile" className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        Profile
                      </Link> */}
                      <button className="dropdown-item logout-btn" onClick={handleLogout}>
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="auth-buttons">
                  <Link to='/login' className="btn btn-primary">
                    Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary">
                    Register
                  </Link>
                </div>
              )}
            </div>

            {/* Mobile Menu Button */}
            <button 
              className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={toggleMobileMenu}
              aria-label="Toggle mobile menu"
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </div>
        </div>

        {/* Mobile Menu Overlay */}
        <div className={`mobile-menu-overlay ${isMobileMenuOpen ? 'active' : ''}`}>
          <div className="mobile-menu-content">
            <div className="mobile-nav-links">
              {navLinks.map(link => (
                <Link
                  key={link.to}
                  to={link.to}
                  className={`mobile-nav-link ${location.pathname === link.to ? 'active' : ''}`}
                >
                  {link.label}
                </Link>
              ))}
            </div>
            <div className="mobile-auth">
              {user ? (
                <div className="user-dropdown-wrapper">
                  <button
                    className="user-btn user-dropdown-toggle"
                    onClick={() => setDropdownOpen(o => !o)}
                    aria-haspopup="true"
                    aria-expanded={dropdownOpen}
                  >
                    <span className="user-avatar">{user.name ? user.name[0].toUpperCase() : 'U'}</span>
                    <span className="user-name">{user.name || 'User'}</span>
                    <span className="dropdown-caret">â–¼</span>
                  </button>
                  {dropdownOpen && (
                    <div className="user-dropdown-menu" onMouseLeave={() => setDropdownOpen(false)}>
                      <Link to={user.role === 'admin' ? '/admin' : '/dashboard'} className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        {user.role === 'admin' ? 'Admin Dashboard' : 'User Dashboard'}
                      </Link>
                      {/* <Link to="/profile" className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        Profile
                      </Link> */}
                      <button className="dropdown-item logout-btn" onClick={handleLogout}>
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="mobile-auth-buttons">
                  <Link to='/login' className="btn btn-primary">
                    Login
                  </Link>
                  <Link to='/register' className="btn btn-secondary">
                    Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Add a mobile bottom nav for logged-in users */}
      {user && (
        <nav className="mobile-bottom-nav-bar">
          {dashboardNavLinks.map(link => (
            <Link key={link.to} to={link.to} className={`bottom-nav-btn${location.pathname === link.to ? ' active' : ''}`}>
              <span className="bottom-nav-icon">{link.icon}</span>
              <span className="bottom-nav-label">{link.label}</span>
            </Link>
          ))}
          <button className="bottom-nav-btn logout" onClick={handleLogout}>
            <span className="bottom-nav-icon">ðŸšª</span>
            <span className="bottom-nav-label">Logout</span>
          </button>
        </nav>
      )}

      {/* Navbar Styles */}
      <style jsx>{`
        .modern-navbar {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: rgba(35, 39, 47, 0.97);
          backdrop-filter: blur(10px);
          border-bottom: 1.5px solid rgba(255, 255, 255, 0.13);
          z-index: 1000;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          min-height: 40px;
          box-shadow: 0 2px 12px rgba(0,0,0,0.10);
        }
        .modern-navbar.scrolled {
          background: rgba(35, 39, 47, 0.99);
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.18);
        }
        .navbar-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 0.2rem 0;
          min-height: 40px;
        }
        .navbar-logo {
          text-decoration: none;
          z-index: 1001;
          margin-right: 1.2rem;
        }
        .logo-text {
          font-weight: 800;
          font-size: clamp(1rem, 2vw, 1.15rem);
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          letter-spacing: 1px;
        }
        .navbar-nav {
          display: flex;
          align-items: center;
          gap: clamp(1.2rem, 3vw, 2.2rem);
        }
        .nav-link {
          color: #E5E7EB;
          text-decoration: none;
          font-weight: 500;
          font-size: clamp(0.85rem, 2vw, 0.95rem);
          padding: 0.2rem 0;
          position: relative;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .nav-link.active,
        .nav-link:hover {
          color: #2ECC71;
        }
        .nav-link.active::after,
        .nav-link:hover::after {
          width: 100%;
        }
        .nav-link::after {
          content: '';
          position: absolute;
          bottom: -2px;
          left: 0;
          width: 0;
          height: 2px;
          background: linear-gradient(135deg, #2ECC71, #FF6B35);
          transition: width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        .navbar-auth {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .user-dropdown-wrapper {
          position: relative;
          display: inline-block;
        }
        .user-btn.user-dropdown-toggle {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background: none;
          border: none;
          color: #E5E7EB;
          font-weight: 600;
          font-size: 0.95rem;
          cursor: pointer;
          padding: 0.2rem 0.7rem 0.2rem 0.5rem;
          border-radius: 2rem;
          transition: background 0.2s;
        }
        .user-btn.user-dropdown-toggle:hover, .user-btn.user-dropdown-toggle:focus {
          background: rgba(46,204,113,0.10);
        }
        .user-avatar {
          width: 1.7rem;
          height: 1.7rem;
          background: linear-gradient(135deg, #2ECC71, #0057D9 80%);
          color: #fff;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1rem;
          font-weight: 700;
          box-shadow: 0 2px 8px rgba(46,204,113,0.13), 0 1px 4px rgba(0,87,217,0.10);
          border: 2px solid #181A20;
        }
        .user-name {
          font-weight: 700;
          color: #E5E7EB;
          letter-spacing: 0.2px;
        }
        .dropdown-caret {
          font-size: 0.8rem;
          margin-left: 0.2rem;
        }
        .user-dropdown-menu {
          position: absolute;
          top: 110%;
          right: 0;
          min-width: 160px;
          background: #23272F;
          border-radius: 1rem;
          box-shadow: 0 8px 32px rgba(0,0,0,0.18);
          padding: 0.5rem 0;
          z-index: 2000;
          animation: dropdownFadeIn 0.2s;
        }
        @keyframes dropdownFadeIn {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .dropdown-item {
          display: block;
          width: 100%;
          padding: 0.6rem 1rem;
          color: #E5E7EB;
          background: none;
          border: none;
          text-align: left;
          font-size: 0.95rem;
          font-weight: 500;
          cursor: pointer;
          transition: background 0.18s;
          text-decoration: none;
        }
        .dropdown-item:hover, .dropdown-item:focus {
          background: rgba(46,204,113,0.10);
          color: #2ECC71;
        }
        .logout-btn {
          color: #FF6B35;
        }
        @media (max-width: 900px) {
          .navbar-content {
            padding: 0.1rem 0.3rem;
          }
          .navbar-nav {
            gap: 0.7rem;
          }
        }
        /* Mobile Styles */
        @media (max-width: 700px) {
          .modern-navbar {
            min-height: 36px;
          }
          .navbar-content {
            padding: 0.1rem 0.1rem;
          }
          .navbar-logo {
            margin-right: 0.7rem;
          }
        }
        .mobile-bottom-nav-bar {
          position: fixed;
          bottom: 0;
          left: 0;
          right: 0;
          background: rgba(35, 39, 47, 0.97);
          backdrop-filter: blur(10px);
          border-top: 1.5px solid rgba(255, 255, 255, 0.13);
          z-index: 2000;
          min-height: 54px;
          display: flex;
          justify-content: space-around;
          align-items: center;
        }
        .bottom-nav-btn {
          flex: 1;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          color: #E5E7EB;
          background: none;
          border: none;
          font-size: 1.1rem;
          font-weight: 600;
          text-decoration: none;
          cursor: pointer;
          transition: color 0.18s, background 0.18s, transform 0.13s;
          height: 100%;
          outline: none;
          border-radius: 0.7rem;
          padding: 0.3rem 0.5rem;
        }
        .bottom-nav-btn:active {
          background: rgba(46,204,113,0.13);
          color: #2ECC71;
          transform: scale(0.97);
        }
        .bottom-nav-btn.active {
          color: #2ECC71;
        }
        .bottom-nav-btn.logout {
          color: #FF6B35;
        }
        .bottom-nav-icon {
          font-size: 1.3rem;
        }
        .bottom-nav-label {
          font-size: 0.7rem;
          margin-top: 2px;
        }
        @media (min-width: 900px) {
          .mobile-bottom-nav-bar {
            display: none;
          }
        }
      `}</style>
    </>
  );
}

export default Navbar;
