import React from 'react';

const features = [
  {
    title: '24/7 Support',
    desc: 'We are always available to help you with any questions or issues.',
    icon: 'üìû',
    color: '#0057D9',
  },
  {
    title: 'On-Time Delivery',
    desc: 'We deliver your projects on time, every time.',
    icon: '‚è∞',
    color: '#FF6B35',
  },
  {
    title: 'Custom Solutions',
    desc: 'Tailored web solutions to fit your unique business needs.',
    icon: 'üõ†Ô∏è',
    color: '#2ECC71',
  },
  {
    title: 'Satisfied Customers',
    desc: 'Our clients are our top priority and we strive for 100% satisfaction.',
    icon: 'üòä',
    color: '#2ECC71',
  },
  {
    title: 'Free Domain',
    desc: 'Get a free domain with every plan.',
    icon: 'üåê',
    color: '#0057D9',
  },
  {
    title: 'Secure Hosting',
    desc: 'Your website is hosted securely with us.',
    icon: 'üîí',
    color: '#FF6B35',
  },
];

function Features() {
  return (
    <div style={{ background: '#F5F5F5', minHeight: '100vh', padding: '0 0 2rem 0' }}>
      <section style={{
        maxWidth: '1100px',
        margin: '0 auto',
        padding: '3rem 1rem',
      }}>
        <h1 style={{ color: '#0057D9', fontWeight: 800, fontSize: '2.5rem', marginBottom: '2.5rem', textAlign: 'center', letterSpacing: '1px' }}>
          Our Features
        </h1>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))',
          gap: '2rem',
        }}>
          {features.map(feature => (
            <div key={feature.title} style={{
              background: '#fff',
              borderRadius: '1.2rem',
              boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
              padding: '2.2rem 1.5rem',
              textAlign: 'center',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
            }}>
              <div style={{ fontSize: '2.8rem', color: feature.color, marginBottom: '1rem' }}>{feature.icon}</div>
              <h2 style={{ color: feature.color, fontWeight: 700, fontSize: '1.3rem', marginBottom: '0.7rem' }}>{feature.title}</h2>
              <p style={{ color: '#2E2E2E', fontSize: '1.05rem', lineHeight: 1.6 }}>{feature.desc}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Features;
